import Handlebars from 'handlebars'

/**
 * Print properties inside html tag
 * @param  {String} attr exemple: atl, title, ...
 * @param  {[type]} value value of the property
 * @return {String} the value to print inside the attribut
 */
export default function attrAbe(attr, value) {
  return new Handlebars.SafeString(value)
}

/**
 * Handlebars helper, to print className and escape it string
 */
export default function className(str) {
  return str.replace(/\.| |\#/g, '_')
}

import {coreUtils} from '../../../'

export default function cleanTab(obj) {
  obj = coreUtils.text.replaceUnwantedChar(obj.replace(/ |&/g, '_'), {
    '\\(': '',
    '\\)': '',
    '\\[': '',
    '\\]': ''
  })

  return obj
}

export default function ifCond(v1, v2, options) {
  v1 = v1 === 'null' ? null : v1
  v2 = v2 === 'null' ? null : v2
  if (v1 == v2) return options.fn(this)
  return options.inverse(this)
}

export default function ifIn(arValues, value, options) {
  for (var arValue in arValues) {
    if (arValue === value) return options.fn(this)
  }
  return options.inverse(this)
}

import extend from 'extend'

/**
 * This helper divide an array into groups of objects and repeat the structure
 * Usage : 
 * 
 *{{#eachgroup products '[{"group":"my","qty":"1"},{"group":"ty","qty":"3"}]'}}
 *    {{my.0.title}} <img src="{{my.0.cover_273x273}}" width="40"><br/>
 *    {{ty.0.title}} <img src="{{ty.0.cover_273x273}}" width="40"><br/>
 *    {{ty.1.title}} <img src="{{ty.1.cover_273x273}}" width="40"><br/>
 *    {{ty.2.title}} <img src="{{ty.2.cover_273x273}}" width="40"><br/>
 *{{/eachgroup}}
 *
 * @param  {[type]} context The array to parse
 * @param  {[type]} id      the groups of groups and qty
 * @param  {[type]} options 
 * @return {[type]}         html
 */
export default function eachGroup(context, groups, options) {
  var fn = options.fn,
    inverse = options.inverse
  var ret = ''
  var group = []
  function getLeaf(node) {
    if (node.group && node.qty) {
      return node
    } else {
      try {
        return getLeaf(node[Object.keys(node)[0]])
      } catch (e) {
        return null
      }
    }
  }

  if (typeof groups !== 'undefined') {
    if (typeof groups === 'string') {
      groups = JSON.parse(groups)
    }
    if (context && context.length > 0) {
      for (
        var i = 0,
          newCount = 0,
          j = context.length,
          distribIndex = 0,
          k = 0,
          iteration = 0;
        i < j;
        i++, newCount++
      ) {
        var o = getLeaf(groups[Object.keys(groups)[distribIndex]])
        if (o == null) break
        var first = newCount % o['qty'] === 0

        // Adding index, isFirst, isLast, isNewGroup to each record
        context[i] = extend(
          true,
          {
            index: i,
            isFirst: first,
            isNewGroup: first && newCount > 0,
            isLast: i === context.length - 1
          },
          context[i]
        )

        if (first) {
          if (newCount > 0) {
            // if the last group has been reached, I reinit the distribIndex
            // and iterate on the result array
            if (distribIndex === Object.keys(groups).length - 1) {
              distribIndex = 0
              var ar = []
            } else {
              distribIndex++
            }
            iteration++
            newCount = 0
            k++
          }

          o = getLeaf(groups[Object.keys(groups)[distribIndex]])
          var key = o['group']
          if (group[iteration] == null) group.push([])
          if (group[iteration][key] == null) {
            group[iteration][key] = []
          }
        }
        group[iteration][key].push(context[i])
      }
      context = group
    }

    if (context) {
      for (var i = 0, j = context.length, k = 0; i < j; i++) {
        ret += fn(context[i])
      }
    } else {
      ret = inverse(this)
    }
  }

  return ret
}

export default function math(lvalue, operator, rvalue) {
  lvalue = parseFloat(lvalue)
  rvalue = parseFloat(rvalue)

  return {
    '+': lvalue + rvalue,
    '-': lvalue - rvalue,
    '*': lvalue * rvalue,
    '/': lvalue / rvalue,
    '%': lvalue % rvalue
  }[operator]
}

/**
 * Handlebars helper, conditionnal modulo
 * @param  {Int} num number to test
 * @param  {Int} mod modulo number
 * @param  {String} block text block content inside of {{#moduloIf}} ... {{/moduloIf}}
 * @return {String|html} if true return the block compiled by handlebar inside our template, if not void
 */
export default function moduloIf(num, mod, block) {
  if (parseInt(num) % parseInt(mod) === 0) {
    return block.fn(this)
  }
  return block.inverse(this)
}

export default function notEmpty(variable, block) {
  if (typeof variable !== 'undefined' && variable !== null && variable !== '') {
    return block.fn(this)
  } else {
    return block.inverse(this)
  }
}

/**
 * Handlebars helper, to print json object
 */
export default function printJson(obj, escapeString) {
  if (typeof obj !== 'undefined' && obj !== null) {
    return typeof escapeString !== 'undefined' &&
    escapeString !== null &&
    escapeString === 1
      ? escape(JSON.stringify(obj).replace(/'/g, '%27'))
      : JSON.stringify(obj).replace(/'/g, '%27')
  } else {
    return '{}'
  }
}

import {coreUtils} from '../../../'
import Handlebars from 'handlebars'

function getVariable(variable, json, options) {
  if (variable.indexOf('{{') > -1) {
    var template = Handlebars.compile(variable)
    var res = template(json)

    if (res === '') {
      template = Handlebars.compile(variable)
      res = template(options.data.root)
    }
    return res
  } else {
    return variable
  }
}

export default function translate(lang, str, json, options) {
  var resLang = getVariable(lang, json, options)
  var resStr = getVariable(str, json, options)

  var trad = coreUtils.locales.instance.i18n
  if (
    typeof trad[resLang] !== 'undefined' &&
    trad[resLang] !== null &&
    typeof trad[resLang][resStr] !== 'undefined' &&
    trad[resLang][resStr] !== null
  ) {
    return trad[resLang][resStr]
  }

  return resStr
}

export default function times(n, block) {
  n = parseInt(n)
  var accum = ''
  for (var i = 0; i < n; ++i) accum += block.fn(i)
  return accum
}

/**
 * Handlebars helper, lowercase the str
 */
export default function lowercase(str) {
  if (typeof str === 'undefined' || str === null) {
    return ''
  } else {
    return str.toLowerCase()
  }
}

/**
 * Handlebars helper, uppercase the str
 */
export default function uppercase(str) {
  if (typeof str === 'undefined' || str === null) {
    return ''
  } else {
    return str.toUpperCase()
  }
}

/**
 * Handlebars helper, to add variable inside template file
 */
export default function setVariable(varName, varValue, options) {
  if (varValue === 'true') {
    varValue = true
  }
  if (varValue === 'false') {
    varValue = false
  }
  if (typeof varValue == 'string' && varValue.indexOf('{') > -1) {
    varValue = JSON.parse(varValue)
  }

  options.data.root[varName] = varValue
}

import {User} from '../../../'

/**
 * Handlebars helper, to print className and escape it string
 */
export default function isAuthorized(route, role, ctx) {
  if (User.utils.isUserAllowedOnRoute(role, route)) {
    return ctx.fn(this)
  } else {
    return ctx.inverse(this)
  }
}

export default function concat() {
  var arg = Array.prototype.slice.call(arguments, 0)
  arg.pop()
  return arg.join('')
}

import Cookies from 'cookies'
import jwt from 'jwt-simple'

import {config, User} from '../../../'

export default function getCurrentuserRole(obj) {
  if (typeof obj.express !== 'undefined' && obj.express !== null) {
    var cookies = new Cookies(obj.express.req, obj.express.res, {
      secure: config.cookie.secure
    })
    var token = cookies.get('x-access-token')
    if (typeof token !== 'undefined' && token !== null && token !== '') {
      var secret = config.users.secret
      var decoded = jwt.decode(token, secret)
      var user = User.utils.findSync(decoded.iss)
      if (user && user.role) return user.role.workflow

      return null
    }
  }
  return ''
}

import fse from 'fs-extra'
import {Promise} from 'bluebird'
import path from 'path'
import {
  config,
  Manager,
  coreUtils,
  cmsData,
  abeExtend,
  cmsTemplates
} from '../../'
import * as sourceAttr from '../../cms/editor/handlebars/sourceAttr'

export function getTemplatesAndPartials(templatesPath, partialsPath) {
  let allFiles = []
  var p = new Promise(resolve => {
    const extension = '.' + config.files.templates.extension
    return coreUtils.file
      .getFiles(templatesPath, true, extension)
      .then(function(files) {
        allFiles = allFiles.concat(files)
        return coreUtils.file
          .getFiles(partialsPath, true, extension)
          .then(function(files) {
            allFiles = allFiles.concat(files)
            return resolve(allFiles)
          })
      })
      .catch(e => {
        console.log('Error: template.getTemplatesAndPartials', e.stack)
      })
  })

  return p
}

export function addOrder(text) {
  var regAbe = /{{abe[\S\s].*?key=['|"]([\S\s].*?['|"| ]}})/g
  var matches = text.match(regAbe)

  if (typeof matches !== 'undefined' && matches !== null) {
    var orderTab = []
    var arrayKey = []
    var max = 0
    var negIndex = -1

    // We create an array of keys/order and put -1 for keys without order
    Array.prototype.forEach.call(matches, match => {
      if (typeof match !== 'undefined' && match !== null) {
        var orderAttr = cmsData.regex.getAttr(match, 'order')
        var keyAttr = cmsData.regex.getAttr(match, 'key')
        if (
          typeof orderAttr === 'undefined' ||
          orderAttr === null ||
          orderAttr === ''
        ) {
          orderTab.push({key: keyAttr, order: negIndex})
          negIndex = negIndex - 1
        } else {
          max = Math.max(parseInt(orderAttr), max)
          orderTab.push({key: keyAttr, order: orderAttr})
        }
      }
    })

    // We sort the array
    orderTab.sort(coreUtils.sort.predicatBy('order', -1))

    // And increment the not ordered ones beginning with the last order found + 1
    Array.prototype.forEach.call(orderTab, obj => {
      if (obj.order < 0) {
        max = max + 1
        arrayKey[obj.key] = max
      } else {
        arrayKey[obj.key] = parseInt(obj.order)
      }
    })

    // Then we put order attribute to each abe tag not ordered
    Array.prototype.forEach.call(matches, match => {
      if (typeof match !== 'undefined' && match !== null) {
        var orderAttr = cmsData.regex.getAttr(match, 'order')
        var keyAttr = cmsData.regex.getAttr(match, 'key')
        if (
          typeof orderAttr === 'undefined' ||
          orderAttr === null ||
          orderAttr === ''
        ) {
          var matchOrder = match.replace(
            /\}\}$/,
            ` order='${arrayKey[keyAttr]}'}}`
          )
          text = text.replace(match, matchOrder)
        }
      }
    })
  }

  return text
}

export function getAbeImport(text) {
  var partials = []
  let listReg = /({{abe.*type=[\'|\"]import.*}})/g
  var match
  while ((match = listReg.exec(text))) {
    partials.push(match[0])
  }

  return partials
}

/**
 * it will include recursively each encountered type=import
 * and proceed to a foreach when the file={{some[]}} is an array of values
 * @param  {[type]} text [description]
 * @param  {[type]} json [description]
 * @return {[type]}      [description]
 */
export function includePartials(text, json) {
  var abeImports = cmsTemplates.template.getAbeImport(text)
  var duplicateImports = duplicateImports || []

  Array.prototype.forEach.call(abeImports, abeImport => {
    var obj = cmsData.attributes.getAll(abeImport, {})

    var file = obj.file
    var partial = ''
    if (file.charAt(0) == '/') {
      file = path.join(config.root, file)
    } else {
      file = path.join(Manager.instance.pathPartials, file)
    }

    file = cmsData.attributes.getValueFromAttribute(file, json)
    if (Object.prototype.toString.call(file) === '[object Array]') {
      Array.prototype.forEach.call(file, f => {
        if (coreUtils.file.exist(f)) {
          if (duplicateImports[f] != null) {
            var tmpFile = fse.readFileSync(f, 'utf8')
            var regAbe = /{{abe .*key=[\'|\"](.*?)[\'|\"].*}}/g
            var matches = tmpFile.match(regAbe)
            var match
            while ((match = regAbe.exec(tmpFile)) !== null) {
              if (match[1] != null) {
                tmpFile = tmpFile.replace(
                  match[0],
                  match[0].replace(
                    match[1],
                    match[1] + '_' + duplicateImports[f]
                  )
                )
                tmpFile = tmpFile.replace(
                  new RegExp(`\{\{${match[1]}`, 'g'),
                  '{{' + match[1] + '_' + duplicateImports[f]
                )
              }
            }
            duplicateImports[f] += 1
            partial += cmsTemplates.template.includePartials(tmpFile, json)
          } else {
            duplicateImports[f] = 1
            partial += cmsTemplates.template.includePartials(
              fse.readFileSync(f, 'utf8'),
              json
            )
          }
        }
      })
    } else {
      if (coreUtils.file.exist(file)) {
        if (duplicateImports[file] != null) {
          var tmpFile = fse.readFileSync(file, 'utf8')
          var regAbe = /{{abe .*key=[\'|\"](.*?)[\'|\"].*}}/g
          var match
          while ((match = regAbe.exec(tmpFile)) !== null) {
            if (match[1] != null) {
              tmpFile = tmpFile.replace(
                match[0],
                match[0].replace(
                  match[1],
                  match[1] + '_' + duplicateImports[file]
                )
              )
              tmpFile = tmpFile.replace(
                new RegExp(`\{\{${match[1]}`, 'g'),
                '{{' + match[1] + '_' + duplicateImports[file]
              )
            }
          }

          duplicateImports[file] += 1
          partial = cmsTemplates.template.includePartials(tmpFile, json)
        } else {
          duplicateImports[file] = 1
          partial = cmsTemplates.template.includePartials(
            fse.readFileSync(file, 'utf8'),
            json
          )
        }
      }
    }
    text = text.replace(cmsData.regex.escapeTextToRegex(abeImport), partial)
  })

  return text
}

export function translate(text) {
  var importReg = /({{abe.*type=[\'|\"]translate.*}})/g

  var matches = text.match(importReg)

  if (typeof matches !== 'undefined' && matches !== null) {
    Array.prototype.forEach.call(matches, match => {
      var splitedMatches = match.split('{{abe ')

      Array.prototype.forEach.call(splitedMatches, splitedMatch => {
        var currentMatch = `{{abe ${splitedMatch}`
        if (/({{abe.*type=[\'|\"]translate.*}})/.test(currentMatch)) {
          var locale = cmsData.regex.getAttr(currentMatch, 'locale')
          var source = cmsData.regex.getAttr(currentMatch, 'source')

          var replace = `{{{i18nAbe "${locale}" "${source}" this}}}`

          text = text.replace(
            cmsData.regex.escapeTextToRegex(currentMatch, 'g'),
            replace
          )
        }
      })
    })
  }

  return text
}

export function getTemplate(file, json = {}) {
  var text = ''

  // HOOKS beforeGetTemplate
  file = abeExtend.hooks.instance.trigger('beforeGetTemplate', file)

  file = file.replace(Manager.instance.pathTemplates, '')
  file = file.replace(config.root, '')
  if (file.indexOf('.') > -1) {
    file = file.replace(/\..+$/, '')
  }
  file = path.join(
    Manager.instance.pathTemplates,
    file + '.' + config.files.templates.extension
  )
  if (coreUtils.file.exist(file)) {
    text = fse.readFileSync(file, 'utf8')
    text = cmsTemplates.template.includePartials(text, json)
    text = cmsTemplates.template.translate(text)
    text = cmsTemplates.template.addOrder(text)
  } else {
    text = `[ ERROR ] template ${file} doesn't exist anymore`
  }

  // HOOKS afterGetTemplate
  text = abeExtend.hooks.instance.trigger('afterGetTemplate', text)

  return text
}

export function getVariablesInWhere(where) {
  var ar = []

  if (where.left.column.indexOf('{{') > -1) {
    ar.push(where.left.column.replace(/\{\{(.*?)\}\}/, '$1'))
  } else {
    ar.push(where.left.column)
  }

  if (where.right.value) {
    if (typeof where.right.value === 'string') {
      if (where.right.value && where.right.value.indexOf('{{') > -1) {
        ar.push(where.right.value.replace(/\{\{(.*?)\}\}/, '$1'))
      }
    } else {
      where.right.value.forEach(function(value) {
        if (value.column.indexOf('{{') > -1) {
          ar.push(value.column.replace(/\{\{(.*?)\}\}/, '$1'))
        }
      })
    }
  }

  if (where.right.column && where.right.column.indexOf('{{') > -1) {
    ar.push(where.right.column.replace(/\{\{(.*?)\}\}/, '$1'))
  }

  return ar
}

/**
 * Get columns and where.left ids of a select statement
 *
 * select title, image from ../ where template=""
 *
 * return [title, image, template]
 *
 * @param  {Array} templatesList ["article.html", "other.html"]
 * @return {Promise}
 */
export function recurseWhereVariables(where) {
  var ar = []
  var arLeft
  var arRight
  switch (where.operator) {
    case 'AND':
      arLeft = cmsTemplates.template.recurseWhereVariables(where.left)
      arRight = cmsTemplates.template.recurseWhereVariables(where.right)
      return arLeft.concat(arRight)
      break
    case 'OR':
      arLeft = cmsTemplates.template.recurseWhereVariables(where.left)
      arRight = cmsTemplates.template.recurseWhereVariables(where.right)
      return arLeft.concat(arRight)
      break
    default:
      ar = getVariablesInWhere(where)
      break
  }

  return ar
}

export function getTemplatesTexts(templatesList, json) {
  var templates = []
  var p = new Promise(resolve => {
    Array.prototype.forEach.call(templatesList, file => {
      var template = fse.readFileSync(file, 'utf8')
      template = cmsTemplates.template.includePartials(template, json)
      var name = file
        .replace(path.join(Manager.instance.pathTemplates, path.sep), '')
        .replace(`.${config.files.templates.extension}`, '')
      templates.push({
        name: name,
        path: file,
        template: template
      })
    })
    resolve(templates)
  })

  return p
}

export function execRequestColumns(tpl) {
  var ar = []
  var matches = cmsData.regex.getTagAbeTypeRequest(tpl)
  Array.prototype.forEach.call(matches, match => {
    var obj = cmsData.attributes.getAll(match[0], {})
    var type = cmsData.sql.getSourceType(obj.sourceString)
    switch (type) {
      case 'request':
        var request = cmsData.sql.handleSqlRequest(obj.sourceString, {})
        if (
          typeof request.columns !== 'undefined' &&
          request.columns !== null
        ) {
          Array.prototype.forEach.call(request.columns, column => {
            ar.push(column)
          })
        }
        if (typeof request.where !== 'undefined' && request.where !== null) {
          ar = ar.concat(
            cmsTemplates.template.recurseWhereVariables(request.where)
          )
        }
    }
  })

  return ar
}

export function getAbeRequestWhereKeysFromTemplates(templatesList) {
  var whereKeys = []
  var p = new Promise(resolve => {
    Array.prototype.forEach.call(templatesList, file => {
      whereKeys = whereKeys.concat(
        cmsTemplates.template.execRequestColumns(file.template)
      )
    })
    whereKeys = whereKeys.filter(function(item, pos) {
      item = item.split('.')[0].split('[')[0]
      return whereKeys.indexOf(item) == pos
    })
    resolve(whereKeys)
  })

  return p
}

export function setAbeSlugDefaultValueIfDoesntExist(templateText) {
  var matches = cmsData.regex.getTagAbeWithType(templateText, 'slug')
  if (matches == null || matches[0] == null) {
    templateText = `{{abe type="slug" source="{{name}}"}}\n${templateText}`
  }

  return templateText
}

export function getAbeSlugFromTemplates(templatesList) {
  var slugs = {}
  Array.prototype.forEach.call(templatesList, file => {
    var templateText = cmsTemplates.template.setAbeSlugDefaultValueIfDoesntExist(
      file.template
    )
    var matchesSlug = cmsData.regex.getTagAbeWithType(templateText, 'slug')
    var obj = cmsData.attributes.getAll(matchesSlug[0], {})
    slugs[file.name] = obj.sourceString
  })
  return slugs
}

/**
 * if there is no abe tab='slug' in the template, add a default abe tag
 * @param {[type]} templateText [description]
 */
export function setAbePrecontribDefaultValueIfDoesntExist(templateText) {
  var matches = cmsData.regex.getTagAbeWithTab(templateText, 'slug')
  if (matches == null || matches[0] == null) {
    templateText = `{{abe type='text' key='name' desc='Name' required="true" tab="slug" visible="false"}}\n${templateText}`
  }

  return templateText
}

/**
 * Lists all templates, in each template:
 * if no tab='slug' replaces the template with default tag {{abe type='text' key='name'...}}
 * then removes all abe tags (but the tab='slug' ones)
 * then adds 'precontribTemplate' with the template name as an attribute to tab='slug' tags
 * @param  {Array} templatesList [description]
 * @return {Array}              [description]
 */
export function getAbePrecontribFromTemplates(templatesList) {
  var precontributionTemplate = []

  Array.prototype.forEach.call(templatesList, file => {
    var templateText = cmsTemplates.template.setAbePrecontribDefaultValueIfDoesntExist(
      file.template
    )
    templateText = templateText.replace(
      /(?!.*?tab=['|"]slug)(\{\{abe.+.*\}\})/g,
      ''
    )
    templateText = templateText.replace(
      /(\{\{abe.+)(\}\})/g,
      `$1 precontribTemplate="${file.name}"$2`
    )
    precontributionTemplate.push(templateText)
  })

  return precontributionTemplate
}

export function getStructureAndTemplates() {
  const extension = '.' + config.files.templates.extension
  let result = {structure: [], templates: []}

  result.structure = coreUtils.file.getFoldersSync(
    Manager.instance.pathStructure,
    true
  )
  let templatePaths = coreUtils.file.getFilesSync(
    Manager.instance.pathTemplates,
    true,
    extension
  )
  Array.prototype.forEach.call(templatePaths, templatePath => {
    let additionalPath = path
      .dirname(templatePath)
      .replace(Manager.instance.pathTemplates, '')
    if (additionalPath !== '') additionalPath = additionalPath.substring(1)
    let name = path.join(additionalPath, path.basename(templatePath, extension))
    let template = {path: templatePath, name: name}
    result.templates.push(template)
  })

  return result
}

export function isSelected(value, display, str) {
  var selected = false

  var pDisplay = prepareDisplay(value, str)
  if (pDisplay === display) {
    selected = true
  }

  return selected
}

export default function sourceAttr(obj, params) {
  var str = params.display
  var selected = ''
  var displayName = prepareDisplay(obj, str)
  var values = params.value
  if (Object.prototype.toString.call(params.value) !== '[object Array]') {
    values = [params.value]
  }

  if (params.multiple != 'multiple') {
    Array.prototype.forEach.call(values, pValue => {
      if (isSelected(pValue, displayName, str)) {
        selected = 'selected'
      }
    })
  }

  return {
    hiddenVal:
      typeof obj == 'object'
        ? JSON.stringify(obj).replace(/\'/g, '&quote;')
        : obj,
    selected: selected,
    val: displayName
  }
}

/**
 * return the value from a json obj of a nested attribute
 * @param  {Object} obj  the json object
 * @param  {string} path the path to object (dot notation)
 * @return {[type]}      the object containing the path object or undefined
 */
export function get(obj, path) {
  if (path == null) {
    return obj
  }
  return path.split('.').reduce(function(prev, curr) {
    return prev ? prev[curr] : undefined
  }, obj || this)
}

/**
 * replace the variables in str by values from obj
 * corresponding to keys
 * @param  {Object} obj    the json object
 * @param  {string} str    the string
 * @return {string}        the string with values
 */
export function prepareDisplay(obj, str = null) {
  var keys = getKeys(str)
  Array.prototype.forEach.call(keys, key => {
    var val = get(obj, key)

    var pattern = new RegExp('{{' + key + '}}|' + key, 'g')
    str = str.replace(pattern, val)
  })
  // console.log('params.value', params.value)

  if (str == null) {
    str = obj
  }

  return str
}

/**
 * return array of variables {{variable}} extracted from str
 * @param  {string} str the string containing variables
 * @return {Array}     the array of variables
 */
export function getKeys(str) {
  var regex = /\{\{(.*?)\}\}/g
  var variables = []
  var match

  while ((match = regex.exec(str)) !== null) {
    if (match[1] != null) {
      variables.push(match[1])
    }
  }

  if (variables.length == 0 && str != null) {
    variables.push(str)
  }

  return variables
}

import Handlebars from 'handlebars'
import striptags from 'striptags'

import {cmsData, config, cmsTemplates} from '../../'

export function addAbeAttrSingleTab(key, elem, htmlAttribute = null) {
  var res = ''

  var valueOfAttritube = key.replace(/\./g, '-')
  key = cmsData.regex.validDataAbe(valueOfAttritube)

  if (htmlAttribute != null) {
    res =
      ' data-abe-attr-' +
      valueOfAttritube +
      '="' +
      htmlAttribute +
      '"' +
      ' data-abe-' +
      valueOfAttritube +
      '="' +
      key +
      '"' +
      elem
  } else {
    res = ' data-abe-' + key + '="' + key + '" ' + elem
  }

  return res
}

export function addAbeAttrForBlock(key, elem, htmlAttribute = null) {
  var res = ''

  var valueOfAttritube = key.split('.')
  var parentKey = valueOfAttritube.shift()
  valueOfAttritube = `${parentKey}[index].${valueOfAttritube[0].replace(
    /\./g,
    '-'
  )}`
  var valueOfAttritubeIndexed = valueOfAttritube.replace(
    /\[index\]/,
    '{{@index}}'
  )
  key = cmsData.regex.validDataAbe(valueOfAttritube)

  if (htmlAttribute) {
    res =
      ` data-abe-attr-${valueOfAttritube}="${htmlAttribute}"  data-abe-${valueOfAttritube}="${key}"` +
      ` data-abe-attr-${valueOfAttritubeIndexed}="${htmlAttribute}" data-abe-${valueOfAttritubeIndexed}="${key}"${elem}`
  } else {
    res =
      ` data-abe-${valueOfAttritube}="${key}"` +
      ` data-abe-${valueOfAttritubeIndexed}="${key}" ${elem}`
  }

  return res
}

/**
 * THIS:
<span>{{abe type='text' key='text_visible'}}</span>

 * BECOMES:
<span data-abe-text_visible="text_visible" >{{abe type='text' key='text_visible'}}</span>

 * @param {[type]} template [description]
 */
export function addAbeDataAttrForHtmlTag(template) {
  var match
  var key
  var getattr
  var newTemplate = template
  var pattern = cmsData.regex.abePattern()

  while ((match = pattern.exec(template))) {
    key = cmsData.regex.getAttr(match, 'key')

    if (!cmsData.regex.isSingleAbe(match, newTemplate)) {
      key = key.replace('.', '{{@index}}.')
    }

    getattr = key.replace(/\./g, '-')

    newTemplate = newTemplate.replace(
      cmsData.regex.escapeTextToRegex(match[0], 'g'),
      ' data-abe-' +
        cmsData.regex.validDataAbe(getattr) +
        '="' +
        getattr +
        '" ' +
        match[0]
    )
  }

  return newTemplate
}

// Is this function useful ? What is it used for ?
export function addHasAbeAttr(text) {
  return text.replace('}}', " has-abe='1'}}")
}

export function getAbeAttributeData(match, text, htmlAttribute, abeTag) {
  var valueOfAttritube
  var key = cmsData.regex.getAttr(match, 'key')
  var res

  if (cmsData.regex.isSingleAbe(match, text)) {
    res = addAbeAttrSingleTab(key, abeTag, htmlAttribute)
  } else {
    res = addAbeAttrForBlock(key, abeTag, htmlAttribute)
  }

  return res
}

/**
 *
 * IF ABE TAG SINGLE (NOT ABE EACH STATEMENT)
 * 
 * THIS:
<img src="{{abe type='image' key='image_key' tab='default'}}" alt="">

 * BECOMES:
<img data-abe-attr-image_key="src" data-abe-image_key="image_key" data-abe-attr-image_key="src"
data-abe-image_key="image_key" src="{{abe type='image' key='image_key' tab='default' has-abe='1'}}" alt="">

 *
 * IF ABE EACH TAG
 * THIS:
{{#each test}}
  <img src="{{abe type='image' key='test.img' desc='test_img' tab='default'}}" alt="">
{{/each}}

 * BECOMES:
{{#each test}}
  <img data-abe-attr-test[index].img="src" data-abe-test[index].img="test[index].img" src="{{abe type='image' key='test.img' desc='test_img' tab='default' has-abe='1'}}" alt="">
{{/each}}

 * @param {[type]} template [description]
 */
export function addAbeDataAttrForHtmlAttributes(template) {
  var text = template.replace(/<([A-Za-z]+)/g, '\nABE_SPLIT<$1')
  let abeTagIntoAttribute = text.match(cmsData.regex.abeAsAttributePattern)

  if (abeTagIntoAttribute != null) {
    Array.prototype.forEach.call(abeTagIntoAttribute, abeIntoTag => {
      let matchAbeTag = /({{abe.*?[\s\S].*?}})/g.exec(abeIntoTag)

      if (matchAbeTag != null && matchAbeTag[1] != null) {
        var toReplace = cmsTemplates.prepare.getAbeAttributeData(
          matchAbeTag[1],
          text,
          abeIntoTag.split('=')[0].trim(),
          abeIntoTag
        )

        toReplace = toReplace.replace(
          cmsData.regex.escapeTextToRegex(matchAbeTag[1]),
          cmsTemplates.prepare.addHasAbeAttr(matchAbeTag[1])
        )

        text = text.replace(
          cmsData.regex.escapeTextToRegex(abeIntoTag),
          toReplace
        )
      }
    })
  }
  text = text.replace(/\nABE_SPLIT</g, '<')

  return text
}

/**
 * Example:
 *
 *
 * THIS:
{{abe type='data' key='data_key' source='select title from article' display='title' editable='true' tab='default'}}

{{#each data_key}}
  {{title}}
{{/each}}

 *
 * BECOMES

{{abe type='data' key='data_key' source='select title from article' display='title' editable='true' tab='default'}}

{{#each data_key}}
  {{title}}
{{/each}}<!-- [[data_key]] %7B%7B%23each%20data_key%7D%7D%0A%09%7B%7Btitle%7D%7D%0A%7B%7B/each%7D%7D -->

 * @param {[type]} template [description]
 * @param {[type]} json     [description]
 */
export function addAbeSourceComment(template, json) {
  if (typeof json.abe_source !== 'undefined' && json.abe_source !== null) {
    var keys = Object.keys(json.abe_source)

    for (var i in keys) {
      var replaceEach = new RegExp(
        `<!-- \\[\\[${keys[i]}\\]\\][\\s\\S]*?-->`,
        'g'
      )
      var regexEachKey = new RegExp(`{{#each ${keys[i]}}}`, 'g')
      template = template.replace(replaceEach, '')

      var patAttrSource = new RegExp(
        ' ([A-Za-z0-9-_]+)=["|\'].*?({{' + keys[i] + '}}).*?["|\']',
        'g'
      )
      var patAttrSourceMatch = template.match(patAttrSource)

      if (patAttrSourceMatch != null) {
        let checkEscapedRegex = /["|'](.*?)["|']/
        let patAttrSourceInside = new RegExp(
          '(\\S+)=["\']?((?:.(?!["\']?\\s+(?:\\S+)=|[>"\']))+.)["\']?({{' +
            keys[i] +
            '}}).*?["|\']',
          'g'
        )
        Array.prototype.forEach.call(patAttrSourceMatch, pat => {
          let patAttrSourceCheck = patAttrSourceInside.exec(pat)
          if (patAttrSourceCheck != null) {
            let checkEscaped = checkEscapedRegex.exec(patAttrSourceCheck[0])
            if (checkEscaped != null && checkEscaped.length > 0) {
              checkEscaped = escape(checkEscaped[1])
              template = template.replace(
                patAttrSourceCheck[0],
                ` data-abe-attr="${patAttrSourceCheck[1]}" data-abe-attr-escaped="${checkEscaped}" data-abe="${keys[
                  i
                ]}" ${patAttrSourceCheck[0]}`
              )
            }
          }
        })
      }

      var eachSource = new RegExp(
        `({{#each ${keys[i]}}[\\s\\S a-z]*?{{\/each}})`,
        'g'
      )
      var matches = template.match(eachSource)
      if (typeof matches !== 'undefined' && matches !== null) {
        Array.prototype.forEach.call(matches, match => {
          template = template.replace(
            match,
            `${match}<!-- [[${keys[i]}]] ${cmsTemplates.encodeAbeTagAsComment(
              match
            )} -->`
          )
        })
      }
    }
  }

  return template
}

//THIS:
//<span>{{abe type='text' key='text_visible'}}</span>
//OR
//<style>{{abe type='text' key='text_visible'}}</style>
//OR
//<script>{{abe type='text' key='text_visible'}}</script>
//BECOMES:
//<span><!--ABE--->{{abe type='text' key='text_visible'}}<!--/ABE---></span>
//OR
//<style>/*<!--ABE--->*/{{abe type='text' key='text_visible'}}/*<!--/ABE--->*/</style>
//OR
//<script>/*<!--ABE--->*/{{abe type='text' key='text_visible'}}/*<!--/ABE--->*/</script>

/**
 * @param {[type]} template [description]
 */
export function addAbeHtmlTagBetweenAbeTags(template) {
  var match
  var match2
  var templateNoDom = striptags(template)
  var pattern = cmsData.regex.abeAsTagPattern()

  // {{#each tags may be declared outside of an html Tag}}
  while ((match = cmsData.regex.eachBlockPattern.exec(template))) {
    template = template.replace(
      cmsData.regex.escapeTextToRegex(match[1], 'g'),
      '<!--ABE--->' + match[1].trim() + '<!--/ABE--->'
    )
  }

  while ((match = pattern.exec(templateNoDom))) {
    template = template.replace(
      cmsData.regex.escapeTextToRegex(match[1], 'g'),
      '<!--ABE--->' + match[1].trim() + '<!--/ABE--->'
    )
  }

  let eachStyles = /<style[\S\s]*?<\/style>/g
  let eachStylePattern = /<style[\S\s]*?(<!--ABE[\S\s]*?--->)[\S\s]*?(<!--\/ABE--->)[\S\s]*?<\/style>/g
  while ((match = eachStyles.exec(template))) {
    var res = match[0]
    while ((match2 = eachStylePattern.exec(res))) {
      res = res
        .replace(
          cmsData.regex.escapeTextToRegex(match2[1], 'g'),
          '/*' + match2[1] + '*/'
        )
        .replace(
          cmsData.regex.escapeTextToRegex(match2[2], 'g'),
          '/*' + match2[2] + '*/'
        )
      template = template.replace(
        cmsData.regex.escapeTextToRegex(match[0], 'g'),
        res
      )
    }
  }

  let eachScripts = /<script[\S\s]*?<\/script>/g
  let eachScriptPattern = /<script[\S\s]*?(<!--ABE[\S\s]*?--->)[\S\s]*?(<!--\/ABE--->)[\S\s]*?<\/script>/g
  while ((match = eachScripts.exec(template))) {
    var res = match[0]
    while ((match2 = eachScriptPattern.exec(res))) {
      res = res
        .replace(
          cmsData.regex.escapeTextToRegex(match2[1], 'g'),
          '/*' + match2[1] + '*/'
        )
        .replace(
          cmsData.regex.escapeTextToRegex(match2[2], 'g'),
          '/*' + match2[2] + '*/'
        )
      template = template.replace(
        cmsData.regex.escapeTextToRegex(match[0], 'g'),
        res
      )
    }
  }

  return template
}

/**
 * THIS:
[index].

 * BECOMES:
{{@index}}-

 *  @param  {[type]} template [description]
 * @return {[type]}          [description]
 */
export function replaceAbeEachIndex(template) {
  return template.replace(/\[index\]\./g, '{{@index}}-')
}

export function removeHiddenAbeTag(template) {
  return template.replace(/(\{\{abe.*visible=[\'|\"]false.*\}\})/g, '')
}

/**
 * Remove {{abe type=*}} from html if attribute visible="false"
 * @param  {[type]} template [description]
 * @return {[type]}          [description]
 */
export function removeHandlebarsRawFromHtml(template) {
  return template.replace(/\{\{\{\{\/?raw\}\}\}\}/g, '')
}

/**
 * split {{#each}}...{{/each}} into an array
 * 
 * @param  {[type]} template [description]
 * @return {[type]}          [description]
 */
export function splitEachBlocks(template) {
  var block
  var blocks = []

  while ((block = cmsData.regex.eachBlockPattern.exec(template))) {
    blocks.push(block[1])
  }

  return blocks
}

export function indexEachBlocks(template, json, onlyHtml) {
  // create an array of {{each}} blocks
  var blocks = cmsTemplates.prepare.splitEachBlocks(template)

  Array.prototype.forEach.call(blocks, block => {
    var key = block.match(/#each (.*?)\}\}/)[1]
    var match

    if (!onlyHtml) {
      var voidData = {}
      voidData[key] = [{}]
      var blockCompiled = Handlebars.compile(
        block
          .replace(/{{abe (.*?["'])[ ]?( has\-abe=1)?}}/g, '[[abe $1]]')
          .replace(
            new RegExp(`\\.\\.\/${config.meta.name}`, 'g'),
            config.meta.name
          )
      )
      var blockHtml = blockCompiled(voidData, {
        data: {intl: config.intlData}
      }).replace(/\[\[abe (.*?)\]\]/g, '{{abe $1}}')

      // je rajoute un data-abe-block avec index sur tous les tags html du bloc each
      var textEachWithIndex = block.replace(
        /(<(?![\/])[A-Za-z0-9!-]*)/g,
        '$1 data-abe-block="' + key + '{{@index}}"'
      )

      // I add an each block commentary only if the each block key != key of a type=data abe tag
      var keys = []
      if (typeof json.abe_source !== 'undefined' && json.abe_source !== null) {
        keys = Object.keys(json.abe_source)
      }
      if (keys.indexOf(key) === -1) {
        template = template.replace(
          block,
          textEachWithIndex +
            `<!-- [[${key}]] ${cmsTemplates.encodeAbeTagAsComment(
              blockHtml
            )} -->`
        )
      }
    }

    // Pour chaque tag Abe
    while ((match = cmsData.regex.abeTag.exec(block))) {
      template = cmsTemplates.prepare.addAbeDictionnary(template, match[0], key)
    }
  })

  return template
}

/**
 * split {{#each}}...{{/each}} into an array
 *
 * THIS:
  {{abe type='text' key='test.title' desc='test title' tab='default'}}

 * BECOMES:
  {{abe dictionnary='test' type='text' key='test.title' desc='test title' tab='default'}}

 * 
 * @param  {[type]} template [description]
 * @return {[type]}          [description]
 */
export function addAbeDictionnary(template, match, key) {
  if (cmsData.regex.isEachStatement(match)) return

  if (cmsData.regex.isBlockAbe(match)) {
    var abeDictionnary = match
      .replace(
        new RegExp('(key=[\'|"])' + key + '.', 'g'),
        '$1' + key + '[index].'
      )
      .replace(/\{\{abe/, "{{abe dictionnary='" + key + "'")

    template = template.replace(match, abeDictionnary)
  }

  return template
}

import fse from 'fs-extra'
import fsCompare from 'fs-compare'
import dircompare from 'dir-compare'
import mkdirp from 'mkdirp'
import path from 'path'

import {coreUtils, cmsTemplates, config, Manager} from '../../'

export function copy() {
  let dest = Manager.instance.pathPublish
  let directory = null
  let source = Manager.instance.pathAssets
  try {
    directory = fse.lstatSync(dest)
    if (!directory.isDirectory() && !directory.isSymbolicLink()) {
      mkdirp.sync(dest)
    }
  } catch (e) {
    mkdirp.sync(dest)
  }

  try {
    const dirSource = fse.lstatSync(source)
    if (!dirSource.isDirectory() && !dirSource.isSymbolicLink()) {
      source = null
    }
  } catch (e) {
    source = null
  }

  directory = fse.lstatSync(dest)
  if (directory.isSymbolicLink()) dest = fse.readlinkSync(dest)

  if (source != null) {
    var res = dircompare.compareSync(source, dest, {
      compareDate: true
    })

    res.diffSet.forEach(function(entry) {
      var state = {
        equal: '==',
        left: '->',
        right: '<-',
        distinct: '<>'
      }[entry.state]

      var name1 = entry.name1 ? entry.name1 : ''
      var name2 = entry.name2 ? entry.name2 : ''

      let exclude = new RegExp(`.${config.files.templates.extension}$`)
      if (
        !exclude.test(name1) &&
        !exclude.test(name2) &&
        entry.type1 !== 'directory' &&
        entry.type2 !== 'directory'
      ) {
        if (typeof entry.path1 !== 'undefined' && entry.path1 !== null) {
          var original = entry.path1
          var basePath = original.replace(Manager.instance.pathAssets, '')
          var move = path.join(dest, basePath)

          if (move === dest) {
            fse.readdir(original, function(res, items) {
              Array.prototype.forEach.call(items, item => {
                var originalItem = path.join(original, item)
                var lstat = fse.lstatSync(originalItem)
                var destItem = path.join(dest, item)

                if (!lstat.isDirectory() && !exclude.test(originalItem)) {
                  try {
                    fsCompare(modifiedTime, originalItem, destItem, function(
                      err,
                      diff
                    ) {
                      if (diff > 0) fse.copySync(originalItem, destItem)
                    })
                  } catch (e) {
                    fse.copySync(originalItem, destItem)
                  }
                }
              })
            })
          } else if (entry.type2 === 'missing' || entry.state === 'distinct') {
            fse.removeSync(move)
            fse.copySync(original, move)
          }
        }
      }
    })
  }

  return true
}

function modifiedTime(fileName, cb) {
  fse.stat(fileName, function(err, stat) {
    if (err) {
      return cb(err)
    }
    return cb(null, stat.mtime)
  })
}

import {cmsData} from '../../'

/**
 * Encode / Escape && add data-abe attributs
 * @param  {String} block
 * @return {String} escaped string
 */
export default function encodeAbeTagAsComment(block) {
  var matchAbe = block.match(/--->\s*\{\{abe .*\}\}/g)
  if (matchAbe) {
    for (var i = 0; i < matchAbe.length; i++) {
      var getattr = cmsData.regex
        .getAttr(matchAbe[i], 'key')
        .replace('.', '[0]-')
      block = block.replace(
        matchAbe[i],
        ' data-abe-' +
          cmsData.regex.validDataAbe(getattr) +
          '="' +
          getattr +
          '" --->'
      )
    }
  }
  matchAbe = block.match(/( [A-Za-z0-9\-\_]+="*{{.*?}})/g)
  if (matchAbe) {
    for (i = 0; i < matchAbe.length; i++) {
      if (typeof matchAbe !== 'undefined' && matchAbe !== null) {
        getattr = cmsData.regex.getAttr(matchAbe[i], 'key').replace('.', '[0]-')
        var matchattr = matchAbe[i].split('=')[0].trim()
        block = block
          .replace(
            matchAbe[i],
            ' data-abe-attr-' +
              cmsData.regex.validDataAbe(getattr) +
              '="' +
              matchattr +
              '"' +
              ' data-abe-' +
              cmsData.regex.validDataAbe(getattr) +
              '="' +
              getattr +
              '" ' +
              matchAbe[i]
          )
          .replace(/\{\{\abe.*?}\}/, '')
      }
    }
  }
  return escape(block)
}

export default function insertDebugtoolUtilities(text, onlyHTML) {
  if (onlyHTML) {
    text = `{{&setVariable "abeEditor" false}}\n${text}`
  } else {
    text = `{{&setVariable "abeEditor" true}}\n${text}`
    text = text.replace(
      /<\/body>/,
      `<style>
          body [data-abe]{ transition: box-shadow 600ms ease-in-out; box-shadow: 0; }
          body .select-border{ border-color: #007CDE; box-shadow: 0 3px 13px #7CBAEF; }
          body img.display-attr:before { content: attr(alt); }
          body a.display-attr:before { content: attr(title); }
          body .display-attr:before { position: absolute; display: block; z-index: 555; font-size: 10px; background-color: rgba(255, 255, 255, 0.75); padding: 2px 5px; color: #5D5D5D; }
          .hidden-abe{ display: none!important; width: 0px !important; height: 0px!important; position: absolute; left: -10000px; top: -10000px; visibility: hidden;}
        </style>
      </body>`
    )
  }
  return text
}

import extend from 'extend'

import {coreUtils} from '../../'

export default class Form {
  constructor() {
    this._form = {}
    this._key = []
  }

  /**
   * Get all input from a template
   * @return {Array} array of input form
   */
  get form() {
    return this._form
  }

  /**
   * Check if key is not in the form array
   * @param  {[type]} key [description]
   * @return {[type]}     [description]
   */
  contains(key) {
    return this._key[key] != null
  }

  /**
   * Add entry to abe engine form
   * @param {String} type        textarea | text | meta | link | image | ...
   * @param {String} key         unique ID, no space allowed
   * @param {String} desc        input description
   * @param {Int}    max-length   maximum characteres allowed inside input
   * @param {String} tab         tab name
   * @param {String} jsonValue   
   * @return {Void}
   */
  add(obj) {
    var defaultValues = {
      autocomplete: null,
      block: '',
      desc: '',
      display: null,
      editable: true,
      key: '',
      'max-length': null,
      order: 0,
      placeholder: '',
      prefill: false,
      'prefill-quantity': null,
      reload: false,
      required: false,
      source: null,
      tab: 'default',
      type: 'text',
      value: null,
      visible: true,
      precontribTemplate: ''
    }

    obj = extend(true, defaultValues, obj)

    if (obj.key.indexOf('[') < 0 && obj.key.indexOf('.') > -1) {
      obj.block = obj.key.split('.')[0]
    }

    if (this._form[obj.tab] == null) this._form[obj.tab] = {item: []}

    this._key[obj.key] = true // save key for contains()
    this._form[obj.tab].item.push(obj)
  }

  orderBlock() {
    var formBlock = {}

    for (var tab in this._form) {
      var formBlockTab = {}
      var formTab = this.orderByGroup(this._form[tab])
      for (var i = 0; i < formTab.item.length; i++) {
        var blockName =
          formTab.item[i].block === '' ? 'default_' + i : formTab.item[i].block
        if (formTab.item[i].key.indexOf('[') > -1) {
          blockName = formTab.item[i].key.split('[')[0]
        }
        if (
          typeof formBlockTab[blockName] === 'undefined' ||
          formBlockTab[blockName] === null
        ) {
          formBlockTab[blockName] = []
        }
        formBlockTab[blockName].push(formTab.item[i])
      }
      if (typeof blockName !== 'undefined' && blockName !== null) {
        formBlockTab[blockName].sort(this.orderByTabindex)
      }
      if (typeof formBlock[tab] === 'undefined' || formBlock[tab] === null) {
        formBlock[tab] = {}
      }

      formBlock[tab] = formBlockTab
    }

    var formTabsOrdered = {}
    var arKeysTabs = Object.keys(formBlock).sort((a, b) => {
      if (
        parseFloat(formBlock[a][Object.keys(formBlock[a])[0]][0].order) <
        parseFloat(formBlock[b][Object.keys(formBlock[b])[0]][0].order)
      ) {
        return -1
      } else if (
        parseFloat(formBlock[a][Object.keys(formBlock[a])[0]][0].order) >
        parseFloat(formBlock[b][Object.keys(formBlock[b])[0]][0].order)
      ) {
        return 1
      }
      return 0
    })

    Array.prototype.forEach.call(arKeysTabs, arKeysTab => {
      if (arKeysTab !== 'slug')
        formTabsOrdered[arKeysTab] = formBlock[arKeysTab]
    })

    formTabsOrdered['slug'] = formBlock['slug']

    return formTabsOrdered
  }

  orderByGroup(group) {
    var finalFields = []
    var groups = {}
    var groupIndex = {}
    var index = 0
    var items = group.item

    // sorting items by order
    items.sort(coreUtils.sort.predicatBy('order'))

    // grouping each group of elements and remembering there index in the finalOrder
    Array.prototype.forEach.call(items, item => {
      if (item.group != null && (item.block === '' || item.block == null)) {
        if (
          typeof groups[item.group] === 'undefined' ||
          groups[item.group] === null
        ) {
          groupIndex[item.group] = index
          groups[item.group] = []
          index++
        }
        groups[item.group].push(item)
      } else {
        finalFields.push(item)
        index++
      }
    })

    // ordering each group with first/last element and inserting the group elements in the final array at the right index.
    var shiftElts = 0
    for (var prop in groups) {
      var group = groups[prop]

      group[0].firstgroup = 1
      group[group.length - 1].lastgroup = 1
      groupIndex[group[0].group] += shiftElts
      shiftElts = shiftElts + group.length - 1

      var args = [groupIndex[group[0].group], 0].concat(group)
      Array.prototype.splice.apply(finalFields, args)
    }

    return {item: finalFields}
  }

  orderByTabindex(a, b) {
    if (a.order < b.order) {
      return -1
    } else if (a.order > b.order) {
      return 1
    }

    return 0
  }
}

import {Promise} from 'bluebird'
import path from 'path'

import {
  coreUtils,
  cmsData,
  cmsEditor,
  abeEngine,
  cmsTemplates,
  abeExtend
} from '../../'

export function add(obj, json, form) {
  var value = obj.value

  if (obj.key.indexOf('[') > -1) {
    var key = obj.key.split('[')[0]
    var index = obj.key.match(/[^\[]+?(?=\])/)[0]
    var prop = obj.key.replace(/[^\.]+?\./, '')

    try {
      obj.value = eval(`json[key][index].${prop}`)
    } catch (e) {
      try {
        eval(`json[key][index].${prop} = ` + JSON.stringify(value))
      } catch (e) {
        // no value found inside json KEY
      }
    }
  } else {
    try {
      if (obj.key.indexOf('.') > -1)
        obj.value = eval(`json["${obj.key.split('.').join('"]["')}"]`)
      else obj.value = eval(`json["${obj.key}"]`)
    } catch (e) {
      // no value found inside json KEY
    }
  }

  if (json != null && json.abe_meta != null) {
    obj.status = json.abe_meta.status
  }

  form.add(obj)

  return obj.value
}

export function addAbeTagToCollection(
  match,
  text,
  json,
  form,
  arrayBlock,
  keyArray = null,
  i = 0
) {
  var obj = cmsData.attributes.getAll(match, json)

  if (typeof keyArray !== 'undefined' && keyArray !== null) {
    // removes the first part of the key. ie. 'main.article' becomes 'article'
    var realKey = obj.key.replace(/[^\.]+?\./, '')

    if (obj.key.indexOf(keyArray + '.') >= 0 && realKey.length > 0) {
      obj.keyArray = keyArray
      obj.realKey = realKey
      obj.key = keyArray + '[' + i + '].' + realKey
      obj.desc = obj.desc + ' ' + i
      insertAbeEach(obj, text, json, form, arrayBlock)
    } else if (!form.contains(obj.key)) {
      obj.value = json[obj.key]
      json[obj.key] = add(obj, json, form)
    }
  }
}

/**
 * parse every abe tags, 
 * create the defaultValue attribute if it finds a value in 'value' attribute
 * If the abe tag object contains a key and is not already put in the form
 * and is not included in a #each statement, I update its value and
 * add it to the form
 *
 * @param  {[type]} text       [description]
 * @param  {[type]} json       [description]
 * @param  {[type]} form       [description]
 * @return {[type]}            [description]
 */
export function addSingleAbeTagsToForm(text, json, form) {
  var match

  while ((match = cmsData.regex.abeTag.exec(text))) {
    var matchText = match[0].replace(
      /value=([\'\"].*?[\'\"])/g,
      'defaultValue=$1'
    )
    var obj = cmsData.attributes.getAll(matchText, json)

    if (
      obj.key != '' &&
      obj.type != '' &&
      !form.contains(obj.key) &&
      cmsData.regex.isSingleAbe(match, text)
    ) {
      if (json[obj.key]) {
        obj.value = json[obj.key]
      } else {
        obj.value = null
      }

      add(obj, json, form)
    }
  }
}

export function insertAbeEach(obj, text, json, form, arrayBlock) {
  if (
    typeof arrayBlock[obj.keyArray][obj.realKey] === 'undefined' ||
    arrayBlock[obj.keyArray][obj.realKey] === null
  ) {
    arrayBlock[obj.keyArray][obj.realKey] = []
  }
  var exist = false
  Array.prototype.forEach.call(arrayBlock[obj.keyArray][obj.realKey], block => {
    if (block.key === obj.key) {
      exist = true
    }
  })
  if (!exist) {
    arrayBlock[obj.keyArray][obj.realKey].push(obj)
  }
}

/**
 * parse every {{#each name}} texts, then select each abe tag included and create
 * an array of these tags in arrayBlock[name]
 * Then reorder these tags and add them to form
 *
 * @param  {[type]} text       [description]
 * @param  {[type]} json       [description]
 * @param  {[type]} form       [description]
 * @param  {[type]} arrayBlock [description]
 * @return {[type]}            [description]
 */
export function addAbeTagCollectionToForm(text, json, form, arrayBlock) {
  var textEach
  var match

  while ((textEach = cmsData.regex.eachBlockPattern.exec(text))) {
    var i
    var keyArray = textEach[2].split(' ')[0]
    var attrArray = []
    var length = 0

    arrayBlock[keyArray] = []

    while ((match = cmsData.regex.abeTag.exec(textEach[0]))) {
      if (json[keyArray]) {
        for (i = 0; i < json[keyArray].length; i++) {
          addAbeTagToCollection(
            match[0],
            text,
            json,
            form,
            arrayBlock,
            keyArray,
            i
          )
        }
      } else {
        addAbeTagToCollection(
          match[0],
          text,
          json,
          form,
          arrayBlock,
          keyArray,
          0
        )
      }
    }

    // Let's reorder the fields and add it to the form
    for (var index in arrayBlock[keyArray]) {
      attrArray.push(index)
      length = arrayBlock[keyArray][index].length
    }

    for (i = 0; i < length; i++) {
      for (var j = 0; j < attrArray.length; j++) {
        add(arrayBlock[keyArray][attrArray[j]][i], json, form)
      }
    }
  }
}

/**
 * Removing all type='data' included in {{#each}} statements
 * Then if editable, obj.value = json[obj.key]
 * else put the source values in the json[obj.key]
 * if not editable, put the json value with values from source
 *
 * @param {[type]} text [description]
 * @param {[type]} json [description]
 * @param {[type]} form [description]
 */
export function addDataAbeTagsToForm(text, json, form) {
  // removing each blocks potentially containing abe data type
  text = text.replace(cmsData.regex.eachBlockPattern, '')

  const matches = cmsData.regex.getTagAbeTypeRequest(text)

  Array.prototype.forEach.call(matches, match => {
    var obj = cmsData.attributes.getAll(match[0], json)

    if (obj.editable) {
      obj.value = json[obj.key]
      add(obj, json, form)
    } else {
      json[obj.key] = obj.source
    }
  })
}

export async function create(text, json, precontrib = false) {
  var form = new cmsEditor.form()
  var arrayBlock = []

  // get all data from type='data' (web service, select, ...)
  // and create a key abe_source with all data
  // + modify keys when editable = false or prefill = true only in the case of source=select
  await cmsData.source.getDataList(text, json)

  // prepare editor values if editable or put values in json from abe_source
  // (don't do this for type='data' included in {{#each}})
  addDataAbeTagsToForm(text, json, form)

  // Remove every abe type='data' tags else but those in {{#each}} statements
  text = cmsData.source.removeNonEachDataList(text)

  if (!precontrib) {
    text = cmsTemplates.template.setAbeSlugDefaultValueIfDoesntExist(text)
    text = cmsTemplates.template.setAbePrecontribDefaultValueIfDoesntExist(
      text
    )
  }

  // add abe tags not in each and with a key to the form
  addSingleAbeTagsToForm(text, json, form)

  // add abe tags in each
  addAbeTagCollectionToForm(text, json, form, arrayBlock)

  if (!precontrib) {
    // HOOKS beforeEditorFormBlocks
    json = abeExtend.hooks.instance.trigger(
      'beforeEditorFormBlocks',
      json,
      text
    )
  }

  var blocks = form.orderBlock()

  if (!precontrib) {
    // HOOKS afterEditorFormBlocks
    blocks = abeExtend.hooks.instance.trigger(
      'afterEditorFormBlocks',
      blocks,
      json,
      text
    )
  }

  abeEngine.instance.content = json
  return {
    form: blocks,
    json: json
  }
}

let singleton = Symbol()
let singletonEnforcer = Symbol()

class abeEngine {
  constructor(enforcer) {
    if (enforcer != singletonEnforcer) throw 'Cannot construct Json singleton'
    this._content = {}
  }

  static get instance() {
    if (!this[singleton]) {
      this[singleton] = new abeEngine(singletonEnforcer)
    }
    return this[singleton]
  }

  get content() {
    return this._content
  }

  set content(content) {
    this._content = content
  }
}

export default abeEngine

import Handlebars from 'handlebars'
import fse from 'fs-extra'
import path from 'path'

import {coreUtils, abeExtend} from '../../../'

export default function abeImport(file, config, ctx) {

  file = abeExtend.hooks.instance.trigger('beforeImport', file, config, ctx)

  var html = ''

  // config de Abe
  config = JSON.parse(config)

  // le path vers les locales
  let intlData = config.intlData

  // le path vers partials
  var defaultPartials = `${__dirname.replace(
    /\/$/,
    ''
  )}/${config.defaultPartials.replace(/\/$/, '')}`

  // si config.custom pas vide, on rajoute ce path to custom
  var custom =
    config.custom !== ''
      ? `${config.root.replace(/\/$/, '')}/${config.custom.replace(/\/$/, '')}`
      : defaultPartials
  var pathToPartial = `${custom}/${file}.html`

  // Je cherche d'abord dans custom puis partials
  // pour récupérer le template
  try {
    fse.statSync(pathToPartial)
  } catch (e) {
    pathToPartial = `${defaultPartials}/${file}.html`
  }
  if (coreUtils.file.exist(pathToPartial)) {
    html = fse.readFileSync(pathToPartial, 'utf8')
  }

  // je recherche les customs de plugins. Si je trouve le fichier aussi dans l'un de ces paths,
  // je rajoute le contenu au html deja obtenu
  var pluginsCustoms = abeExtend.plugins.instance.getCustoms()
  Array.prototype.forEach.call(pluginsCustoms, pluginCustom => {
    var checkFile = path.join(pluginCustom, `${file}.html`)
    if (coreUtils.file.exist(checkFile)) {
      html += fse.readFileSync(checkFile, 'utf8')
    }
  })

  // Je fais pareil avec le path des partials des plugins
  var pluginsPartials = abeExtend.plugins.instance.getPartials()
  Array.prototype.forEach.call(pluginsPartials, pluginPartials => {
    var checkFile = path.join(pluginPartials, `${file}.html`)
    if (coreUtils.file.exist(checkFile)) {
      html += fse.readFileSync(checkFile, 'utf8')
    }
  })

  html = abeExtend.hooks.instance.trigger(
    'afterImport',
    html,
    file,
    config,
    ctx
  )

  // Et je finis par compiler le template !!!!
  var template = Handlebars.compile(html)
  var res = new Handlebars.SafeString(template(ctx, {data: {intl: intlData}}))

  return res
}

import Handlebars from 'handlebars'
import abeEngine from './abeEngine'
import xss from 'xss'

import {config} from '../../../'

/**
 * Abe handlebar helper, that retrieve text to add to handlebars templating engine
 * @return {String} the string to replace {{ handlebars_key }}
 */
export default function compileAbe() {
  var content = abeEngine.instance.content
  if (arguments[0].hash['key'] == null) return ''
  var key
  var hash
  var value
  var testXSS

  if (arguments[0].hash['key'].indexOf('}-') > 0) {
    key = arguments[0].hash['key'].split('-')
    key = key[key.length - 1]
    hash = arguments[0].hash
    // hash.key = hash.key.replace(/\{\{@index\}\}/, '[{{@index}}]')
    hash.key = hash.key.replace(
      /\{\{@index\}\}/,
      `[${arguments[0].data.index}]`
    )
    try {
      value = content
        ? eval(
            `content["${hash.dictionnary}"][${arguments[0].data.index}].${key}`
          )
        : hash.key
      // value = content ? content[hash['dictionnary']][arguments[0].data.index][key] : hash.key
    } catch (e) {
      console.log(e.stack)
      value = ''
    }
    if (typeof value === 'function' || value == null) {
      value = hash.value != null ? hash.value : ''
    }
    if (hash.type != null && (hash.type === 'rich' || hash.type === 'code')) {
      if (config.xss) {
        testXSS = xss(value.replace(/&quot;/g, '"'), {
          whiteList: config.htmlWhiteList,
          stripIgnoreTag: true
        })
        return new Handlebars.SafeString(testXSS)
      } else {
        return new Handlebars.SafeString(value.replace(/&quot;/g, '"'))
      }
    }

    return value.replace(/%27/, "'")
  }

  key = arguments[0].hash['key'].replace('.', '-')

  hash = arguments[0].hash
  if (content) {
    try {
      if (hash.key.indexOf('.') > -1)
        value = eval(`content["${hash.key.split('.').join('"]["')}"]`)
      else value = eval(`content["${hash.key}"]`)
    } catch (e) {
      value = ''
    }
  } else {
    value = hash.key
  }

  if (typeof value === 'function' || value == null) {
    value = hash.value != null ? hash.value : ''
  }

  if (hash.type != null && (hash.type === 'rich' || hash.type === 'code')) {
    if (config.xss) {
      testXSS = xss(value.replace(/&quot;/g, '"'), {
        whiteList: config.htmlWhiteList,
        stripIgnoreTag: true
      })
      return new Handlebars.SafeString(testXSS)
    } else {
      return new Handlebars.SafeString(value.replace(/&quot;/g, '"'))
    }
  }

  return value.replace(/%27/, "'")
}

import path from 'path'
import recursiveFolder from './recursiveFolder'

export default function folders(obj, index, link, translate) {
  var res
  if (obj && obj.length > 0) {
    if (link != null && link !== 'null') {
      var links = link.replace(/^\//, '').split(path.sep)
      links.pop()
      res = recursiveFolder(obj, 1, '', links, false, translate)
    } else {
      res = recursiveFolder(obj, 1, '', null, false, translate)
    }
  }
  return res
}

import path from 'path'

export default function recursiveFolder(
  obj,
  index = 1,
  dataShow = '',
  links = null,
  visible = false,
  translate
) {
  var classHidden = ''
  if (index > 1 && !visible) {
    classHidden = 'hidden'
  }

  var id = `level-${index}`
  var parent = obj[0] ? obj[0].path.split('/')[0] : ''
  var levelText =
    translate && translate['level-' + index] != null
      ? translate['level-' + index]
      : 'Level ' + index
  var res = `<div class="form-group level-${index} ${classHidden}" data-parent="${parent}" data-shown="${dataShow}">
    <label for="${id}" class="control-label">${levelText}</label>
      <select data-precontrib="true" data-slug="true" data-slug-type="path" data-show-hide-sub-folder="true" id="${id}" data-id="${id}" class="form-control">
        <option data-level-hide="${index + 1}"></option>`

  var sub = ''

  Array.prototype.forEach.call(obj, o => {
    let selected = ''
    let isVisible = false
    let name = path.basename(o.path)
    if (links != null && links[index - 1] != null) {
      if (links[index - 1] === name) {
        selected = 'selected="selected"'
        isVisible = true
      }
    }
    res += `<option ${selected} data-level-hide="${index +
      2}" data-level-show="${index + 1}" data-show="${name.replace(
      /\.| |\#/g,
      '_'
    )}">${name}</option>`

    if (o.folders != null && o.folders.length > 0) {
      sub += recursiveFolder(
        o.folders,
        index + 1,
        name.replace(/\.| |\#/g, '_'),
        links,
        isVisible,
        translate
      )
    }
  })

  res += `</select>
  </div>`

  res += sub

  return res
}

import Handlebars from 'handlebars'
import moment from 'moment'
import {math, abeExtend, config} from '../../../'

export default function listPage(file, index, text) {
  var res = ''
  file = abeExtend.hooks.instance.trigger('beforeListPage', file, index, text)

  res += '<tr>'
  res += `<td>${math(index, '+', 1)}</td>
        <td>
          <a href="/abe/editor${file.abe_meta.link}" class="file-path">
            ${file.abe_meta.link}
          </a>
        </td>`

  if (file.abe_meta.template) {
    res += `<td align="center">
              ${file.abe_meta.template}
            </td>`
  } else {
    res += '<td align="center"></td>'
  }

  if (file.date) {
    var dateSearch = moment(file.date).utcOffset(0).format('YYYY-MM-DD')
    var dateOrder = new Date(file.date).getTime()
    res += `<td align="center" data-search="${dateSearch}" data-order="${dateOrder}">
              ${dateSearch}
            </td>`
  } else {
    res += '<td align="center" data-search="0000-00-00" data-order="0"></td>'
  }

  var workflow = ''

  var status = file.abe_meta.status
  var workflowUser = config.users.workflow
  Array.prototype.forEach.call(workflowUser, flow => {
    var hidden = ''
    if (status !== flow) {
      hidden = 'hidden'
    }

    workflow += `<td align="center" class="${flow}">`
    if (file[flow]) {
      var fDate = moment(file[flow].date)
        .utcOffset(0)
        .format('YYYY-MM-DD HH:mm:ss')
      if (flow === 'publish') {
        workflow += `<a href="/abe/editor${file[flow]
          .link}" class="checkmark label-published" title="${fDate}">&#10004;</a>`
      } else {
        workflow += `<a href="/abe/editor${file[flow]
          .link}" class="${hidden} label label-default label-draft" title="${fDate}">${flow}</a>`
      }
    } else {
    }
    workflow += '</td>'
  })

  res += workflow

  res += `<td align="center">
            <div>`

  if (file.publish != null) {
    res += `<a href="/abe/operations/unpublish${file.abe_meta.link}"
               title="${text.unpublish}"
               class="icon" data-unpublish="true" data-text="${text.confirmUnpublish} ${file
      .abe_meta.link}"
               title="unpublish">
              <span class="fa fa-eye-slash"></span>
            </a>`
  }

  res += `<a href="/abe/operations/delete/${file.abe_meta.status}${file.abe_meta
    .link}"
             title="${text.delete}"
             class="icon"
             data-delete="true"
             data-text="${text.confirmDelete} ${file.abe_meta.link}"
             title="remove">
            <span class="fa fa-trash"></span>
          </a>`

  res += `
        </div>
      </td>
    </tr>`

  res = abeExtend.hooks.instance.trigger(
    'afterListPage',
    res,
    file,
    index,
    text
  )
  return new Handlebars.SafeString(res)
}

import {printInput} from './printInput'
import abeEngine from './abeEngine'

export default function printBlock(ctx, root) {
  var res = ''
  var precontrib = false
  if (root.precontrib != null) {
    precontrib = root.precontrib
  }

  if (ctx[0].block != null && ctx[0].block !== '') {
    let tmpContent = ''
    let hidden = true
    Array.prototype.forEach.call(ctx, item => {
      if (item.editable) hidden = false
      if (precontrib) item.value = ''
      tmpContent += printInput(item, root)
    })

    if (!hidden)
      res += `<div class="form-group" data-precontrib-templates="${ctx[0]
        .precontribTemplate}">
                <label class="title">
                  ${ctx[0].group != null ? ctx[0].group : ctx[0].block}
                </label>
                <div class='single-block well well-sm'>`
    else
      res += `<div class="form-group" data-precontrib-templates="${ctx[0]
        .precontribTemplate}">
                <div class='single-block'>`
    res += tmpContent
    res += '</div></div>'
  } else if (ctx[0].key.indexOf('[') > -1) {
    var ctxBlock = ctx[0].key.split('[')[0]
    res += `<div class="form-group" data-precontrib-templates="${ctx[0]
      .precontribTemplate}">
              <div class="list-group" data-block="${ctxBlock}" >
                <label class="d-flex justify-content-between">
                  ${ctxBlock}
                  <button type="button" class="btn btn-success add-block" title="Add new block" >
                    <span class="fa fa-plus" aria-hidden="true"></span>
                  </button>
                </label>`

    var arrItem = []
    Array.prototype.forEach.call(ctx, item => {
      var index = item.key.match(/[^\[\]]+?(?=\])/)
      if (arrItem[index] == null) {
        arrItem[index] = []
      }
      arrItem[index].push(item)
    })

    Array.prototype.forEach.call(Object.keys(arrItem), i => {
      var key = arrItem[i][0].key.split('[')[0]
      var display = ''
      if (
        abeEngine.instance.content[key] == null ||
        abeEngine.instance.content[key].length === 0
      ) {
        display = 'style="display: none"'
      }
      res += `<div class="list-block" data-block="${key}${i}" ${display}>
                <button type="button" class="btn btn-info collapsed" data-toggle="collapse" data-target="#${key}${i}" >
                  Section <span class='label-count'>${i}</span> :
                  <span class="fa fa-chevron-down" aria-hidden="true"></span>
                </button>
                <button type="button" class="btn btn-danger remove-block" title="Delete block" >
                  <span class="fa fa-trash" aria-hidden="true"></span>
                </button>
                <div id="${key}${i}" class="collapse" >
                `
      Array.prototype.forEach.call(arrItem[i], item => {
        if (precontrib) item.value = ''
        res += printInput(item, root)
      })
      res += '</div></div>'
    })

    res += `
          </div>
        </div>`
  } else {
    if (precontrib) ctx[0].value = ''
    res += printInput(ctx[0], root)
  }

  return res
}

import sourceAutocomplete from './sourceAutocomplete'
import sourceOption from './sourceOption'
import {abeExtend, User, cmsData} from '../../../'

export function getAttributes(params) {
  var attributes = ''
  if (params.key != null)
    attributes += `id="${params.key}" data-id="${params.key}"`
  if (params.value != null) attributes += ` value="${params.value}"`
  if (params['max-length'] != null)
    attributes += ` data-maxlength="${params['max-length']}"`
  if (params.reload != null) attributes += ` reload="${params.reload}"`
  if (params.order != null) attributes += ` tabIndex="${params.order}"`
  if (params.required != null)
    attributes += ` data-required="${params.required}"`
  if (params.display != null) attributes += ` data-display="${params.display}"`
  if (params.visible != null) attributes += ` data-visible="${params.visible}"`
  if (params.autocomplete != null)
    attributes += ` data-autocomplete="${params.autocomplete}"`
  if (params.placeholder != null)
    attributes += ` placeholder="${params.placeholder}"`
  if (params.thumbs != null) attributes += ` data-size="${params.thumbs}"`
  if (params.toolbar != null) attributes += ` data-toolbar="${params.toolbar}"`
  if (params.multiple != null)
    attributes += ` data-multiple="${params.multiple}"`
  if (params.disabled != null) attributes += ` ${params.disabled}`
  return attributes
}

export function getLabel(params) {
  var desc = params.desc + (params.required ? ' *' : '')
  return `<label class="control-label" for="${params.key}" >
            ${desc}
          </label>`
}

export function hint(params) {
  if (params.hint) {
    return `<p class="abe-hint help-block">
        <span class="fa fa-info-sign"></span>&nbsp;<em>${params.hint}</em>
      </p>`
  }

  return ''
}

export function createInputSource(attributes, inputClass, params) {
  var inputSource = `<div class="parent-${params.type} parent-${params.key}" data-parent="${params.key}">`
  var lastValues

  if (
    (params.autocomplete != null && params.autocomplete === 'true') ||
    (params.multiple != null && params.multiple === 'multiple')
  ) {
    if (cmsData.sql.getSourceType(params.sourceString) === 'url')
      lastValues = params.source
    else lastValues = JSON.stringify(params.source).replace(/\'/g, '&quote;')
    inputSource += '<div class="autocomplete-result-wrapper">'
    if (
      params.autocomplete != null &&
      params.autocomplete === 'true' &&
      params.prefill === 'true'
    ) {
      inputSource += `<div  class="autocomplete-refresh" value=''
                            data-autocomplete-refresh="true"
                            data-autocomplete-refresh-sourcestring="${params.sourceString}"
                            data-autocomplete-refresh-prefill-quantity="${params[
                              'prefill-quantity'
                            ]}"
                            data-autocomplete-refresh-key="${params.key}"
                            data-autocomplete-data-display="${params.display}" >
                        <span class="fa fa-refresh"></span>
                      </div>`
    }
    Array.prototype.forEach.call(params.value, val => {
      inputSource += sourceAutocomplete(val, params)
    })
    inputSource += '</div>'
  }

  if (params.autocomplete != null && params.autocomplete === 'true') {
    inputSource += `<input value="" type="text" autocomplete="off" data-value='${lastValues}' ${attributes} class="${inputClass}" />`
  } else {
    // lastValues = JSON.stringify(params.value).replace(/\'/g, '&quote;')
    inputSource += `<select ${attributes} class="${inputClass}">`

    // if (!params.required) inputSource += '<option value=\'\'></option>'
    var options = ''
    if (
      typeof params.source === 'object' &&
      Object.prototype.toString.call(params.source) === '[object Array]'
    ) {
      Array.prototype.forEach.call(params.source, val => {
        options += sourceOption(val, params)
      })
    } else {
      options += sourceOption(params.source, params)
    }

    var defaultValueSelected = 'selected=selected'
    if (options.indexOf('selected') > -1) {
      defaultValueSelected = ''
    }
    if (params.required)
      inputSource += `<option value="" disabled ${defaultValueSelected}>Select ${params.desc.toLowerCase()}...</option>`
    if (!params.required)
      inputSource += `<option value="" ${defaultValueSelected}></option>`
    inputSource += options

    inputSource += '</select>'
  }
  inputSource += `${hint(params)}</div>`

  return inputSource
}

