            if (typeof this.lserver != 'undefined') {
              tinylr.changed(f)
            }
            this.events.reference.emit('update')
          })
          monitor.on('changed', f => {
            this.updateReferences(f)
            if (typeof this.lserver != 'undefined') {
              tinylr.changed(f)
            }
            this.events.reference.emit('update')
          })
          monitor.on('removed', f => {
            this.updateReferences()
            if (typeof this.lserver != 'undefined') {
