  getKeysFromSelect() {
    this._whereKeys = []
    var p = new Promise(resolve => {
      cmsTemplates.template
        .getTemplatesAndPartials(this.pathTemplates, this.pathPartials)
        .then(
          templatesList => {
            return cmsTemplates.template
              .getTemplatesTexts(templatesList)
              .then(
                templatesText => {
                  return cmsTemplates.template
                    .getAbeRequestWhereKeysFromTemplates(templatesText)
                    .then(
                      async whereKeys => {
                        this._whereKeys = whereKeys
                        this._slugs = cmsTemplates.template.getAbeSlugFromTemplates(
                          templatesText
                        )
                        this._precontribution = cmsTemplates.template.getAbePrecontribFromTemplates(
                          templatesText
                        )
                        await this.updateList()
                        resolve()
                      },
                      e => {
                        console.log(
                          'Reject: Manager.findRequestColumns',
                          e.stack
                        )
                      }
                    )
                    .catch(e => {
                      console.log('Error: Manager.findRequestColumns', e.stack)
                    })
                },
                e => {
                  console.log('Reject: Manager.getTemplatesTexts', e.stack)
                }
              )
              .catch(e => {
                console.log('Error: Manager.getTemplatesTexts', e.stack)
              })
          },
          e => {
            console.log('Manager.getKeysFromSelect', e.stack)
          }
        )
        .catch(e => {
          console.log('Manager.getKeysFromSelect', e.stack)
        })
    })

    return p
  }

  getStructureAndTemplates() {
    return this._structureAndTemplates
  }

  updateStructureAndTemplates() {
    this._structureAndTemplates = cmsTemplates.template.getStructureAndTemplates()
  }

  getThumbsList() {
    if (typeof this._thumbs === 'undefined' || this._thumbs === null)
      this._thumbs = cmsMedia.image.getThumbsList()
    return this._thumbs
  }

  addThumbsToList(thumb) {
    if (this._thumbs) this._thumbs.push(thumb)
  }

  getReferences() {
    if (typeof this._references === 'undefined' || this._references === null)
      this.updateReferences()
    return this._references
  }

  getPrecontribution() {
    return this._precontribution
  }

  getSlugs() {
    return this._slugs
  }

  updateReferences(referenceName) {
    var references = cmsReference.reference.getFiles()
    if (referenceName && references[referenceName])
      this._references[referenceName] = references[referenceName]
    else this._references = references

    return this
  }

  getList() {
    return this._list
  }

  getListWithStatusOnFolder(status, subPath = '') {
    let list = []
    const subPathFull = `${subPath.replace(/^\/|\/$/g, '')}/`

    Array.prototype.forEach.call(this._list, file => {
      if (
        typeof file[status] !== 'undefined' &&
        file[status] !== null &&
        ( subPath === '' || file.path.startsWith(subPathFull))
      ) {
        list.push(file)
      }
    })

    return list
  }

  setList(list) {
    this._list = list

    return this
  }

  /**
   * return true if postUrl is found in the Manager list
   * @param {String} postUrl The url path of the file
   */
  postExist(postUrl) {
    const docPath = cmsData.utils.getDocRelativePathFromPostUrl(postUrl)
    const found = coreUtils.array.find(this._list, 'path', docPath)

    if (found.length > 0) {
      return true
    }
    return false
  }

  /**
   * When a post is modified or created, this method is called so that the manager updates the list with the updated/new item
   * @param {String} revisionPath The full path to the post
   */
  async updatePostInList(revisionPath) {
    const docPath = cmsData.utils.getDocRelativePath(revisionPath)
    const found = coreUtils.array.find(this._list, 'path', docPath)
    const json = await cmsData.revision.getDoc(revisionPath)
    let index
    let merged = {}
    let revision = cmsData.revision.getFileObject(revisionPath, json)
    revision = cmsData.file.getAbeMeta(revision, json)
    Array.prototype.forEach.call(this._whereKeys, key => {
      var keyFirst = key.split('.')[0]
      revision[keyFirst] = json[keyFirst]
    })

    if (found.length > 0) {
      index = found[0]
      merged[docPath] = this._list[index]
      // If I publish, I remove the previous published versions
      if (revision.abe_meta.status === 'publish') {
        Array.prototype.forEach.call(
          merged[docPath].revisions,
          (revision, revIndex) => {
            if (revision.abe_meta.status === 'publish') {
              merged[docPath].revisions.splice(revIndex, 1)
            }
          }
        )
      }
      merged[docPath].revisions.push(JSON.parse(JSON.stringify(revision)))
      const sortedResult = cmsData.revision.sortRevisions(merged)
      // Does the publish version has been removed (in the case of unpublish) ?
      if (
        sortedResult[0]['publish'] &&
        !cmsData.revision.exist(sortedResult[0]['publish']['path'])
      ) {
        delete sortedResult[0]['publish']
      }
      this._list[index] = sortedResult[0]
    } else {
      index = this._list.length
      let rev = []
      rev.push(revision)
      merged = cmsData.revision.mergeRevisions(rev)
      const sortedResult = cmsData.revision.sortRevisions(merged)
      this._list.push(sortedResult[0])
    }

    this._list.sort(coreUtils.sort.predicatBy('date', -1))
    this.historize(index)
  }

  /**
   * When data.history is set, we do keep the number of revisions = history in the most recent date order
   * @param  {[type]} index [description]
   * @return {[type]}       [description]
   */
  historize(index) {
    if (config.data.history && config.data.history > 0) {
      let arStatus = []
      if (this._list[index].revisions.length > config.data.history) {
        Array.prototype.forEach.call(
          this._list[index].revisions,
          (revision, revIndex) => {
            if (revIndex >= config.data.history) {
              if (revision.abe_meta.status === 'publish') {
                if (arStatus.indexOf(revision.abe_meta.status) < 0) {
                  arStatus.push(revision.abe_meta.status)
                } else {
                  this._list[index].revisions.splice(revIndex, 1)
                }
              } else {
                this._list[index].revisions.splice(revIndex, 1)
                cmsOperations.remove.removeRevision(revision.path)
              }
            } else if (arStatus.indexOf(revision.abe_meta.status) < 0) {
              arStatus.push(revision.abe_meta.status)
            }
          }
        )
      }
    }
  }

  /**
   * When a post is deleted, this method is called so that the manager updates the list with the removed item
   * @param {String} postUrl The URL of the post
   */
  removePostFromList(postUrl) {
    const docPath = cmsData.utils.getDocRelativePathFromPostUrl(postUrl)
    this._list = coreUtils.array.removeByAttr(this._list, 'path', docPath)
  }

  /**
   * This method loads all posts into an array with values used in "select" statements from templates
   * + abe_meta data
   * @return {Array} Array of objects representing the posts including their revisions
   */
  async updateList() {
    this._list = await cmsData.revision.getAllWithKeys(this._whereKeys)
    this._list.sort(coreUtils.sort.predicatBy('date', -1))
    console.log('Manager updated')
  }

  getPage(
    start = 0,
    length = 20,
    sortField = 'date',
    sortDir = -1,
    search = '',
    searchFields = ['abe_meta.link', 'abe_meta.template', 'name']
  ) {
    const total = this._list.length
    let totalFiltered = total
    let list = this._list.slice()

    if (search !== '') {
      const searches = search.split(' ')
      for (var i = 0; i < searches.length; i++) {
        list = coreUtils.array.facet(list, searchFields, searches[i])
      }

      totalFiltered = list.length
    }

    if (sortField != 'date' || sortDir != -1) {
      list.sort(coreUtils.sort.predicatBy(sortField, sortDir))
    }

    list = list.slice(start, start + length)

    return {
      recordsTotal: total,
      recordsFiltered: totalFiltered,
      data: list
    }
  }

  addProcess(name) {
    this._processesRunning[name] = true
  }

  removeProcess(name) {
    delete this._processesRunning[name]
  }

  isProcessRunning(name) {
    if (
      this._processesRunning[name] !== null &&
      this._processesRunning[name] === true
    ) {
      return true
    } else {
      return false
    }
  }

  getActivities() {
    return User.utils.getActivity()
  }

  addActivity(activity) {
    User.utils.addActivity(activity)
  }

  getConnections() {
    return this.connections
  }

  addConnection(res) {
    this.connections.push(res)
  }

  removeConnection(res) {
    var i = this.connections.indexOf(res)
    if (i !== -1) {
      this.connections.splice(i, 1)
    }
  }
}

export default Manager

import Handlebars from 'handlebars'
import path from 'path'
import fse from 'fs-extra'

import {abeEngine, cmsData, cmsTemplates, config, abeExtend, Manager} from '../'

/**
 * Page class
 * manage HTML generation for page template
 */
export default class Page {
  /**
   * Create new page object
   * @param  {Object} params req.params from express route
   * @param  {Object} i18n translation
   * @param  {Function} callback 
   * @param  {Boolean} onlyHTML default = true, if false HTML content will be prepared for edition (contains abe attributes)
   * @return {String} HTML page as string
   */
  constructor(template, json, onlyHTML = true) {
    json = abeExtend.hooks.instance.trigger('beforePageJson', json)

    abeEngine.instance.content = json

    this._onlyHTML = onlyHTML
    this.template = template

    // Remove text with attribute "visible=false"
    this.template = cmsTemplates.prepare.removeHiddenAbeTag(this.template)

    // remove abe type="slug" from template
    this.template = this.template.replace(/({{abe.*type=[\'|\"]slug.*}})/g, '')
    if (!this._onlyHTML) {
      // Surrounds each Abe tag (which are text/rich/textarea and not in html attribute) with <abe> tag
      // ie. <title><abe>{{abe type='text' key='meta_title' desc='Meta title' tab='Meta' order='4000'}}</abe></title>
      this.template = cmsTemplates.prepare.addAbeHtmlTagBetweenAbeTags(
        this.template
      )

      this.template = cmsTemplates.prepare.addAbeDataAttrForHtmlAttributes(
        this.template
      )

      this.template = cmsTemplates.prepare.addAbeDataAttrForHtmlTag(
        this.template
      )

      this.template = cmsTemplates.prepare.addAbeSourceComment(
        this.template,
        json
      )
    } else {
      this.template = cmsTemplates.prepare.removeHandlebarsRawFromHtml(
        this.template
      )
    }

    // je rajoute les index pour chaque bloc lié à un each
    this.template = cmsTemplates.prepare.indexEachBlocks(
      this.template,
      json,
      this._onlyHTML
    )

    // We remove the {{abe type=data ...}} from the text
    this.template = cmsData.source.removeDataList(this.template)

    // It's time to replace the [index] by {{@index}} (concerning each blocks)
    this.template = cmsTemplates.prepare.replaceAbeEachIndex(this.template)

    // I compile the text
    var compiledTemplate = Handlebars.compile(
      cmsTemplates.insertDebugtoolUtilities(this.template, this._onlyHTML)
    )

    // I create the html page ! yeah !!!
    this.html = compiledTemplate(json, {data: {intl: config.intlData}})

    if (this._onlyHTML) {
      this.html = abeExtend.hooks.instance.trigger(
        'afterPageSaveCompile',
        this.html,
        json
      )
    } else {
      this.html = abeExtend.hooks.instance.trigger(
        'afterPageEditorCompile',
        this.html,
        json
      )
    }
  }
}

import fse from 'fs-extra'
import clc from 'cli-color'
import extend from 'extend'
import path from 'path'

import config from './config.json'

var result = config
result.root = process.cwd()
if (process.env.ROOT) {
  result.root = process.env.ROOT.replace(/\/$/, '')
}

var hintAbeJson = false

var loadLocalConfig = result => {
  const abeConfigPath = path.join(result.root, 'abe.json')
  if (result.root !== '') {
    try {
      var stat = fse.statSync(result.root)
      if (stat && stat.isDirectory()) {
        try {
          stat = fse.statSync(abeConfigPath)
          if (stat) {
            var json = fse.readJsonSync(abeConfigPath)
            result = extend(true, result, json)
          }
        } catch (e) {
          if (!hintAbeJson) {
            hintAbeJson = true
            // console.log(
            //   clc.green('[ Hint ]'),
            //   'you can create a specific config file named abe.json to customize your abe install',
            //   clc.cyan.underline('https://github.com/abecms/abecms/blob/master/docs/abe-config.md')
            // )
          }
        }
      }
    } catch (e) {
      console.log('LoadConfig Error')
      console.log(e)
    }
  }
}

loadLocalConfig(result)

result.exist = (conf, json) => {
  var c = conf.split('.')
  var current = json
  if (typeof current !== 'undefined' && current !== null) {
    Array.prototype.forEach.call(c, c => {
      if (
        current !== false &&
        typeof current[c] !== 'undefined' &&
        current[c] !== null
      ) {
        current = current[c]
      } else {
        current = false
        return false
      }
    })
    return current
  } else {
    return false
  }
}

result.localConfigExist = () => {
  if (result.root !== '') {
    try {
      var stat = fse.statSync(result.root)
      if (stat && stat.isDirectory()) {
        try {
          stat = fse.statSync(path.join(result.root, 'abe.json'))
          if (stat) {
            return true
          }
        } catch (e) {
          return false
        }
      }
    } catch (e) {
      return false
    }
  }

  return false
}

result.getLocalConfig = () => {
  if (result.localConfigExist()) {
    return fse.readJsonSync(path.join(result.root, 'abe.json'))
  } else {
    return {}
  }
}

result.getDefault = conf => {
  return result[conf]
}

result.get = conf => {
  return result.exist(conf, result)
}

result.set = json => {
  extend(true, result, json)
  loadLocalConfig(result)
}

result.extend = json => {
  const localConf = result.getLocalConfig()
  extend(true, localConf, json)
  result.save(localConf)
}

result.save = json => {
  const confPath = path.join(result.root, 'abe.json')
  fse.writeJsonSync(confPath, json, {space: 2, encoding: 'utf-8'})
  loadLocalConfig(result)
}

result.getConfigByWebsite = () => {
  var defaultConfig = extend(true, {}, result)
  var configBySite = {
    default: {}
  }

  var localConfig = extend(true, {}, defaultConfig)
  for (var item in localConfig) {
    switch (item) {
      case 'intlData':
        configBySite.default.intlData = localConfig[item]
        break
      case 'templates':
        configBySite.default.templates = localConfig[item]
        break
      case 'structure':
        configBySite.default.structure = localConfig[item]
        break
      case 'data':
        configBySite.default.data = localConfig[item]
        break
      case 'publish':
        configBySite.default.publish = localConfig[item]
        break
      case 'files':
        configBySite.default.files = {
          templates: {
            extension: localConfig[item].templates.extension
          }
        }
        break
      case 'upload':
        configBySite.default.upload = localConfig[item]
        break
    }
  }

  return configBySite
}

export default result

import { config, mongo, cmsData, abeExtend, coreUtils, Manager } from "../../";
import path from "path";
import { MongoClient } from "mongodb";
import _ from "lodash";

const options = {
  useNewUrlParser: true,
  poolSize: 10
};

const url = `${_.get(
  config,
  "database.mongo.server",
  "mongodb://localhost"
)}:${_.get(config, "database.mongo.port", 27017)}/${_.get(
  config,
  "database.mongo.database",
  "abe"
)}`;

let _db;

export function connectToServer(callback) {
  if (!url) {
    return callback("Missing mongo URL");
  }
  MongoClient.connect(url, options, function(err, client) {
    if (!client || err) {
      return callback(err);
    }
    _db = client.db(_.get(config, "database.mongo.database", "abe"));
    return callback(err);
  });
}

export function getDb() {
  return _db;
}

export async function getDoc(jsonPath) {
  jsonPath = cmsData.utils.getRevisionRelativePath(jsonPath);
  const db = mongo.getDb();
  const JSONs = db.collection("jsons");
  let json = {};

  try {
    const doc = await JSONs.findOne({ jsonPath });
    if (doc) {
      json = doc.json;
      json.path = jsonPath;
    } else {
      json = { err: true };
    }
  } catch (e) {
    console.error(e);
  }

  return json;
}

export async function getAllWithKeys(withKeys) {
  var db = mongo.getDb();
  var JSONs = db.collection("jsons");

  let docs = await JSONs.find().toArray();

  let docsArr = [];

  for (var i in docs) {
    let fjson = docs[i].json;
    let fileObject = {
      date: "" + docs[i].mtime.toString() + "",
      path: docs[i].jsonPath
    };
    fileObject = cmsData.file.getAbeMeta(fileObject, fjson);

    docsArr.push(fileObject);
  }

  return docsArr;
}

