import {parse} from 'node-sqlparser'
import {Promise} from 'bluebird'
import path from 'path'
import _ from 'lodash'
import {coreUtils, config, Manager, cmsData} from '../../'

/**
 * take a string and json to escape sql character and convert to sql like syntax
 *
 * Example: escapeAbeValuesFromStringRequest('select title from ./ where `abe_meta.template`=`{{article}}`', {article: "test"})
 *
 * Return string: select title from ___abe_dot______abe_dot______abe___ where  `abe_meta.template`=`test`
 *
 *
 * @param  {String} str      raw abe request sql string
 * @param  {Object} jsonPage json object of post
 * @return {String}          escaped string
 */
export function escapeAbeValuesFromStringRequest(str, jsonPage) {
  var matchFrom = /from .(.*?) /
  var matchVariable = /{{(.*?)}}/

  var matchFromExec = matchFrom.exec(str)
  if (matchFromExec != null && matchFromExec[1] != null) {
    var fromMatch
    var toReplace = matchFromExec[1]
    while ((fromMatch = matchVariable.exec(toReplace))) {
      try {
        var arFrom = fromMatch[1].split('.')
        var value = _.get(jsonPage, arFrom)
        if (value != null) {
          toReplace = toReplace.replace('{{' + fromMatch[1] + '}}', value)
        } else {
          toReplace = toReplace.replace('{{' + fromMatch[1] + '}}', '')
        }
      } catch (e) {}
    }

    str = str.replace(matchFromExec[1], toReplace)
  }

  var from = /from ([\S\s]+)/.exec(str)

  var matches = from
  if (matches[1]) {
    var res = matches[1]
    var splitAttr = [
      ' where ',
      ' order by ',
      ' limit ',
      ' WHERE ',
      ' ORDER BY ',
      ' LIMIT '
    ]
    for (var i = 0; i < splitAttr.length; i++) {
      if (res.indexOf(splitAttr[i]) > -1) {
        res = res.substring(0, res.indexOf(splitAttr[i]))
      }
    }
    var escapedFrom = res.replace(/\//g, '___abe___')
    escapedFrom = escapedFrom.replace(/\./g, '___abe_dot___')
    escapedFrom = escapedFrom.replace(/-/g, '___abe_dash___')
    str = str.replace(res, escapedFrom)
  }

  str = str.replace(/``/g, "''")

  return str
}

/**
 * analyse and create an object from request sql string
 *
 * Example: handleSqlRequest('select title from ./ where `abe_meta.template`=`{{article}}`', {article: "test"})
 *
 * @param  {String} str      Sql string request
 * @param  {Object} jsonPage json of post
 * @return {Object}          {type, columns, from, where, string, limit, orderby}
 */
export function handleSqlRequest(str, jsonPage) {
  var req = escapeAbeValuesFromStringRequest(str, jsonPage)
  var request = parse(req)
  var reconstructSql = ''
  // SQL TYPE
  var type = ''
  if (request.type != null) {
    type = request.type
  }
  reconstructSql += `${type} `
  // SQL COLUMNS
  var columns = []
  if (request.columns != null) {
    if (request.columns === '*') {
      columns.push('*')
    } else {
      Array.prototype.forEach.call(request.columns, item => {
        if (_.get(item, 'expr.type') === 'binary_expr') {
          columns.push(`${item.expr.left.column}${item.expr.operator}${item.expr.right.column}`)
        } else {
          columns.push(item.expr.column)
        }
      })
    }
  }
  reconstructSql += `${JSON.stringify(columns)} `
  // SQL FROM
  var from = []
  if (request.from != null) {
    Array.prototype.forEach.call(request.from, item => {
      from.push(item.table)
    })
  } else {
    from.push('*')
  }
  reconstructSql += `from ${JSON.stringify(from)} `

  var where = null
  if (request.where != null) {
    where = request.where
  }

  var limit = -1
  if (request.limit != null) {
    limit = request.limit[request.limit.length - 1].value
  }

  var orderby = null
  if (request.orderby != null && request.orderby.length > 0) {
    orderby = {
      column: request.orderby[0].expr.column,
      type: request.orderby[0].type
    }
    reconstructSql += `ORDER BY ${orderby.column} ${orderby.type} `
  }

  return {
    type: type,
    columns: columns,
    from: from,
    where: where,
    string: reconstructSql,
    limit: limit,
    orderby: orderby
  }
}

/**
 * get JSON from abe tag attribute source
 *
 * {{abe type='data' key='titles' desc='select titles' source='[{"title": "rouge", "id": 1},{"title": "vert", "id": 2},{"title": "blue", "id": 3}]' display="{{title}}"}}
 *
 * return
 * [{"title": "rouge", "id": 1},{"title": "vert", "id": 2},{"title": "blue", "id": 3}]
 *
 * @param  {String} str abe tag
 * @return {String}     json string
 */
export function getDataSource(str) {
  var reg = /source=(['|"])(.*?)\1[ |\}]/g
  var match = reg.exec(str)
  if (match != null) {
    return match[2]
  }

  return ''
}

/**
 * replaces escaped characters with the right ones
 * @param  {String} statement the from clause
 * @return {String}           the from sanitized
 */
export function sanitizeFromStatement(statement) {
  var from = ''

  if (statement != null) {
    from = statement[0].replace(/___abe_dot___/g, '.')
    from = from.replace(/___abe___/g, '/')
    from = from.replace(/___abe_dash___/g, '-')
  }

  return from
}

/**
 * calculate the directory to analyze from the from clause
 * @param  {String} statement the from clause
 * @param  {String} tplPath   the path from the template originator
 * @return {string}           the directory to analyze
 */
export function getFromDirectory(statement, tplPath = '/') {
  var pathFromDir = ''

  if (statement === '' || statement === '*' || statement === '/') {
    pathFromDir = Manager.instance.pathData
  } else if (statement === './') {
    pathFromDir = path.join(Manager.instance.pathData, tplPath)
  } else if (statement.indexOf('/') === 0) {
    pathFromDir = path.join(Manager.instance.pathData, statement)
  } else if (statement.indexOf('/') !== 0) {
    pathFromDir = path.join(Manager.instance.pathData, tplPath, statement)
  }

  return pathFromDir
}

/**
 * sort array of files from where clause
 *
 * @param  {Array} files
 * @param  {Object} orderby {orderby: {column: 'date'}} | {orderby: {column: 'random', type: 'ASC'}}
 * @return {Array}         sorted array
 */
export function executeOrderByClause(files, orderby) {
  if (orderby != null) {
    if (orderby.column.toLowerCase() === 'random') {
      files = coreUtils.sort.shuffle(files)
    } else if (orderby.column.toLowerCase() === 'date') {
      if (orderby.type.toLowerCase() === 'asc') {
        files.sort(cmsData.sort.byDateAsc)
      } else if (orderby.type.toLowerCase() === 'desc') {
        files.sort(cmsData.sort.byDateDesc)
      }
    } else {
      files.sort(
        coreUtils.sort.predicatBy(
          orderby.column.toLowerCase(),
          orderby.type.toLowerCase() === 'desc' ? -1 : 1
        )
      )
    }
  }

  return files
}

/**
 * Keep only published post
 *
 * keepOnlyPublishedPost([files])
 *
 * @param  {Array} files      paths
 * @return {Array}                files
 */
export function keepOnlyPublishedPost(files) {
  var publishedValue = []
  Array.prototype.forEach.call(files, file => {
    if (file.publish != null) {
      publishedValue.push(file.publish)
    }
  })

  return publishedValue
}

/**
 * Check array of files have path that match path statement
 *
 * executeFromClause([array], ['/'], ['/'])
 *
 * @param  {Array} statement      paths
 * @param  {Array} pathFromClause paths
 * @return {Array}                files
 */
export function executeFromClause(files, statement, pathFromClause) {
  var from = sanitizeFromStatement(statement)

  // if the from clause ends with a dot, we won't recurse the directory analyze
  if (from.slice(-1) === '.') {
    from = from.slice(0, -1)
  }

  var fromDirectory = getFromDirectory(from, pathFromClause)

  var files_array = files.filter(element => {
    if (element.path.indexOf(fromDirectory) > -1) {
      return true
    }
    return false
  })

  return files_array
}

/**
 * Execute sql query like to find abe json post that match the query
 *
 * @param  {Array} pathQuery of paths
 * @param  {String} match     request sql
 * @param  {Object} jsonPage  json of post
 * @return {Array}           found object that match
 */
export function execQuery(match, jsonPage) {
  var files = keepOnlyPublishedPost(Manager.instance.getList())
  var request = handleSqlRequest(
    cmsData.regex.getAttr(match, 'source'),
    jsonPage
  )
  var pathQuery =
    jsonPage && jsonPage.abe_meta != null && jsonPage.abe_meta.link != null
      ? path.dirname(jsonPage.abe_meta.link)
      : '/'

  files = executeFromClause(files, request.from, pathQuery)
  files = executeWhereClause(files, request.where, request.columns, jsonPage)
  files = executeOrderByClause(files, request.orderby)
  files = executeLimitClause(files, request.limit)
  return files
}

export function executeQuerySync(match, jsonPage) {
  return execQuery(match, jsonPage)
}

export function executeQuery(match, jsonPage) {
  var p = new Promise(resolve => {
    var res = execQuery(match, jsonPage)
    resolve(res)
  }).catch(function(e) {
    console.error(e)
  })

  return p
}

/**
 * check if a given string an url, string json, file url, abe sql request
 *
 * get('http://google.com')
 * get('{"test":"test"}')
 * get('select * from ../')
 * get('test')
 *
 * @param  {String} str
 * @return {String} url | request | value | file | other
 */
export function getSourceType(str) {
  if (/^(http:\/\/|https:\/\/|:\/\/|\/(.*)\?(.*)[^ ]=)/.test(str)) {
    return 'url'
  }

  if (/select[\S\s]*?from/.test(str)) {
    return 'request'
  }

  try {
    JSON.parse(str)
    return 'value'
  } catch (e) {}

  if (/\.json/.test(str)) {
    return 'file'
  }

  return 'other'
}

/**
 * return array of post that match sql where statement
 *
 * Example: handleSqlRequest('select title from ./ where `abe_meta.template`=`article`', {})
 *
 * @param  {Array} files
 * @param  {Object} wheres   clause
 * @param  {Int} maxLimit
 * @param  {Array} columns  sql
 * @param  {Object} jsonPage json post
 * @return {Array}          of files
 */
export function executeWhereClause(files, wheres, columns, jsonPage) {
  if (typeof wheres === 'undefined' || wheres === null) return files
  var res = []
  var json = {}
  var jsonValues = {}

  for (let file of files) {
    if (wheres != null) {
      if (!recurseWhere(wheres, file, jsonPage)) {
        json = JSON.parse(JSON.stringify(file))
        jsonValues = {}

        if (columns != null && columns.length > 0 && columns[0] !== '*') {
          Array.prototype.forEach.call(columns, column => {
            if (json[column] != null) {
              jsonValues[column] = json[column]
            }
          })
          jsonValues['abe_meta'] = json['abe_meta']
        } else {
          jsonValues = json
        }

        res.push(jsonValues)
      }
    }
  }

  return res
}

/**
 * return array of post in the limit
 *
 * Example: executeLimitClause({}, 2)
 *
 * @param  {Array} files
 * @param  {Int} maxLimit
 * @return {Array} of files
 */
export function executeLimitClause(files, maxLimit) {
  if (files.length > maxLimit && maxLimit > 0) {
    files.splice(maxLimit, files.length - maxLimit)
  }

  return files
}

/**
 * Compare where left and where right clause
 *
 * @param  {Object} where           clause
 * @param  {Object} jsonDoc         json of current post
 * @param  {Object} jsonOriginalDoc json of post to compare
 * @return {Object}                 {left: value, right: value}
 */
export function getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc) {
  var regexIsVariable = /^{{(.*)}}$/
  var value = null
  var compare = null

  try {
    var variableLeft = where.left.column
    var whereLeftColumn = where.left.column
    var checkIfLeftIsAVariable = regexIsVariable.exec(variableLeft)
    if (checkIfLeftIsAVariable != null && checkIfLeftIsAVariable.length > 0) {
      variableLeft = checkIfLeftIsAVariable[1]
    }
    var arVariableLeft = variableLeft.split('.')
    value = _.get(jsonDoc, arVariableLeft)
  } catch (e) {
    // console.log('e', e)
  }

  if (where.operator === 'IN' || where.operator === 'NOT IN') {
    if (value == null) {
      // is the form of variableLeft something like elt.attribute or elt[].attribute?
      // so maybe is elt an array ?
      let arValues = variableLeft.split('.')
      let key = arValues[0]
      if (key.slice(-2) == '[]') {
        key = key.slice(0, -2)
      }
      let records = _.get(jsonDoc, key)
      whereLeftColumn = arValues[1]
      value = []
      // if yes, value is then an array of values
      if (records != null) {
        Array.prototype.forEach.call(records, record => {
          try {
            let val = record[arValues[1]]
            value.push(val)
          } catch (e) {
            console.log(e.stack)
          }
        })
      }
    }
    compare = []
    Array.prototype.forEach.call(where.right.value, right => {
      var matchRightVariable = regexIsVariable.exec(right.column)
      if (matchRightVariable != null && matchRightVariable.length > 0) {
        try {
          var jsonOriginalValues = _.get(jsonOriginalDoc, matchRightVariable[1])
          if (
            Object.prototype.toString.call(jsonOriginalValues) ===
            '[object Array]'
          ) {
            Array.prototype.forEach.call(
              jsonOriginalValues,
              jsonOriginalValue => {
                compare.push(_.get(jsonOriginalValue, whereLeftColumn))
              }
            )
          } else {
            compare.push(jsonOriginalValues)
          }
        } catch (e) {}
      } else {
        compare.push(right.column)
      }
    })
  } else {
    if (where.right.column != null) {
      compare = where.right.column
    } else if (where.right.value != null) {
      compare = where.right.value
    }

    var matchRightVariable = regexIsVariable.exec(compare)

    if (matchRightVariable != null && matchRightVariable.length > 0) {
      try {
        var arVariableRight = matchRightVariable[1].split('.')
        var shouldCompare = _.get(jsonOriginalDoc, arVariableRight)
        if (shouldCompare != null) {
          compare = shouldCompare
        } else {
          compare = null
        }
      } catch (e) {
        compare = null
      }
    }
  }

  return {
    left: value,
    right: compare
  }
}

export function isInStatementCorrect(values, isCorrect) {
  let hasPassedTest = false
  if (Object.prototype.toString.call(values.left) === '[object Array]') {
    Array.prototype.forEach.call(values.left, left => {
      Array.prototype.forEach.call(values.right, right => {
        if (left != null && left === right) {
          hasPassedTest = true
        }
      })
    })
  } else {
    Array.prototype.forEach.call(values.right, right => {
      if (values.left === right) {
        hasPassedTest = true
      }
    })
  }

  if (hasPassedTest) isCorrect = !isCorrect

  return isCorrect
}

/**
 * Check where.left value that match where operator (=, !=, >, >=, <, <=, LIKE, NOT LIKE, AND, OR, IN, NOT IN)
 * if operator AND or OR
 * Recurse on where.left and where.right sql clause
 *
 *
 * @param  {Object} where           clause
 * @param  {Object} jsonDoc         json of current post
 * @param  {Object} jsonOriginalDoc json of post to compare
 * @return {Boolean}                 true if not matching | false if matching
 */
export function recurseWhere(where, jsonDoc, jsonOriginalDoc) {
  var isNotLeftCorrect = false
  var isNotRightCorrect = false
  var isNotCorrect = false
  var values

  switch (where.operator) {
    case '=':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left === values.right)
      break
    case '!=':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left !== values.right)
      break
    case '>':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left > values.right)
      break
    case '>=':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left >= values.right)
      break
    case '<':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left < values.right)
      break
    case '<=':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left <= values.right)
      break
    case 'LIKE':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left && values.left.indexOf(values.right) > -1)
      break
    case 'NOT LIKE':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = !(values.left && values.left.indexOf(values.right) === -1)
      break
    case 'AND':
      isNotLeftCorrect = recurseWhere(where.left, jsonDoc, jsonOriginalDoc)
      isNotRightCorrect = recurseWhere(where.right, jsonDoc, jsonOriginalDoc)
      isNotCorrect = isNotLeftCorrect || isNotRightCorrect ? true : false
      break
    case 'OR':
      isNotLeftCorrect = recurseWhere(where.left, jsonDoc, jsonOriginalDoc)
      isNotRightCorrect = recurseWhere(where.right, jsonDoc, jsonOriginalDoc)
      isNotCorrect = isNotLeftCorrect && isNotRightCorrect ? true : false
      break
    case 'IN':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = isInStatementCorrect(values, true)
      break
    case 'NOT IN':
      values = getWhereValuesToCompare(where, jsonDoc, jsonOriginalDoc)
      isNotCorrect = isInStatementCorrect(values, false)
      break
  }

  return isNotCorrect
}

import {Promise} from 'bluebird'
import http from 'http'
import https from 'https'
import path from 'path'
import fse from 'fs-extra'
import _ from 'lodash'

import {config, coreUtils, cmsData} from '../../'

export function requestList(obj, match, jsonPage) {
  var p = new Promise(resolve => {
    cmsData.sql.executeQuery(match, jsonPage).then(data => {
      if (!jsonPage['abe_source']) {
        jsonPage['abe_source'] = {}
      }
      jsonPage['abe_source'][obj.key] = data

      // I update the jsonPage[obj.key] when the tag is not editable
      if (!obj.editable) {
        if (obj['max-length']) {
          jsonPage[obj.key] = data.slice(0, obj['max-length'])
        } else {
          jsonPage[obj.key] = data
        }
      // I update the jsonPage[obj.key] only if empty or obj.prefill
      } else if (jsonPage[obj.key] == null && obj.prefill) {
        if (obj['prefill-quantity'] && obj['max-length']) {
          jsonPage[obj.key] = data.slice(
            0,
            obj['prefill-quantity'] > obj['max-length']
              ? obj['max-length']
              : obj['prefill-quantity']
          )
        } else if (obj['prefill-quantity']) {
          jsonPage[obj.key] = data.slice(0, obj['prefill-quantity'])
        } else if (obj['max-length']) {
          jsonPage[obj.key] = data.slice(0, obj['max-length'])
        } else {
          jsonPage[obj.key] = data
        }
      }

      resolve(jsonPage)
    })
  })

  return p
}

export function valueList(obj, match, jsonPage) {
  var p = new Promise(resolve => {
    var value = cmsData.sql.getDataSource(match)

    if (value.indexOf('{') > -1 || value.indexOf('[') > -1) {
      try {
        value = JSON.parse(value)

        jsonPage['abe_source'][obj.key] = value
      } catch (e) {
        jsonPage['abe_source'][obj.key] = null
        console.log(`Error ${value}/is not a valid JSON`, `\n${e}`)
      }
    }
    resolve()
  })

  return p
}

export function urlList(obj, match, jsonPage) {
  var p = new Promise(resolve => {
    if (obj.autocomplete !== true && obj.autocomplete !== 'true') {
      var host = obj.sourceString
      host = host.split('/')
      var httpUse = http
      var defaultPort = 80
      if (host[0] === 'https:') {
        httpUse = https
        defaultPort = 443
      }
      host = host[2].split(':')

      var pathSource = obj.sourceString.split('//')
      if (pathSource[1] != null) {
        pathSource = pathSource[1].split('/')
        pathSource.shift()
        pathSource = '/' + pathSource.join('/')
      } else {
        pathSource = '/'
      }
      var options = {
        hostname: host[0],
        port: host[1] != null ? host[1] : defaultPort,
        path: pathSource,
        method: 'GET',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Content-Length': 0
        }
      }

      var body = ''

      var localReq = httpUse.request(options, localRes => {
        localRes.setEncoding('utf8')
        localRes.on('data', chunk => {
          body += chunk
        })
        localRes.on('end', () => {
          try {
            if (typeof body === 'string') {
              var parsedBody = JSON.parse(body)
              if (
                typeof parsedBody === 'object' &&
                Object.prototype.toString.call(parsedBody) === '[object Array]'
              ) {
                jsonPage['abe_source'][obj.key] = parsedBody
              } else if (
                typeof parsedBody === 'object' &&
                Object.prototype.toString.call(parsedBody) === '[object Object]'
              ) {
                jsonPage['abe_source'][obj.key] = [parsedBody]
              }
            } else if (
              typeof body === 'object' &&
              Object.prototype.toString.call(body) === '[object Array]'
            ) {
              jsonPage['abe_source'][obj.key] = body
            } else if (
              typeof body === 'object' &&
              Object.prototype.toString.call(body) === '[object Object]'
            ) {
              jsonPage['abe_source'][obj.key] = body
            }
          } catch (e) {
            console.log(
              `Error ${obj.sourceString} is not a valid JSON`,
              `\n${e}`
            )
          }
          resolve()
        })
      })

      localReq.on('error', e => {
        console.log(e)
      })

      // write data to request body
      localReq.write('')
      localReq.end()
    } else {
      jsonPage['abe_source'][obj.key] = obj.sourceString
      resolve()
    }
  })

  return p
}

export function fileList(obj, match, jsonPage) {
  var p = new Promise(resolve => {
    jsonPage['abe_source'][obj.key] = coreUtils.file.getJson(
      path.join(config.root, obj.sourceString)
    )
    resolve()
  })

  return p
}

export async function nextSourceData(jsonPage, match) {
    let obj = cmsData.attributes.getAll(match, jsonPage)
    const sourceType = cmsData.sql.getSourceType(obj.sourceString)

    if (sourceType === 'file' && obj.type !== 'data' && obj.type !== 'import') {
      obj = cmsData.attributes.sanitizeSourceAttribute(obj, jsonPage)
      let file = obj.sourceString

      if (file.charAt(0) == '/') {
        file = path.join(config.root, file)
      } else {
        file = path.join(config.root, config.reference.url, file)
      }

      // Do I have to save data
      if (_.get(jsonPage, obj.key) != null) {
        let newJson = {}
        if (coreUtils.file.exist(file)) {
          newJson = await cmsData.file.get(file)
          if(_.get(newJson, obj.key) !== _.get(jsonPage, obj.key)) {
            _.set(newJson, obj.key, _.get(jsonPage, obj.key));
            await fse.writeJson(file, newJson)
          }
        } else {
          _.set(newJson, obj.key, _.get(jsonPage, obj.key));
          await fse.mkdir(file.split('/').slice(0, -1).join('/'))
          await fse.writeJson(file, newJson, {space: 2, encoding: 'utf-8'})
        }
        _.unset(jsonPage, obj.key);
      // Or do I have to only read data
      } else {
        if (coreUtils.file.exist(file)) {
          const newJson = await cmsData.file.get(file)

          if (_.get(newJson, obj.key) != null) {
            _.set(jsonPage, obj.key, _.get(newJson, obj.key))
          }
        }
      }
    }

}

export function nextDataList(jsonPage, match) {
  var p = new Promise(resolve => {
    if (jsonPage['abe_source'] == null) {
      jsonPage['abe_source'] = {}
    }

    var obj = cmsData.attributes.getAll(match, jsonPage)

    var type = cmsData.sql.getSourceType(obj.sourceString)

    switch (type) {
      case 'request':
        obj = cmsData.attributes.sanitizeSourceAttribute(obj, jsonPage)
        requestList(obj, match, jsonPage)
          .then(jsonPage => {
            resolve(jsonPage)
          })
          .catch(e => {
            console.log('[ERROR] source.js requestList', e)
          })
        break
      case 'value':
        obj = cmsData.attributes.sanitizeSourceAttribute(obj, jsonPage)
        valueList(obj, match, jsonPage)
          .then(() => {
            resolve()
          })
          .catch(e => {
            console.log('[ERROR] source.js valueList', e)
          })
        break
      case 'url':
        urlList(obj, match, jsonPage)
          .then(() => {
            resolve()
          })
          .catch(e => {
            console.log('[ERROR] source.js urlList', e)
          })
        break
      case 'file':
        obj = cmsData.attributes.sanitizeSourceAttribute(obj, jsonPage)
        fileList(obj, match, jsonPage)
          .then(() => {
            resolve()
          })
          .catch(e => {
            console.log('[ERROR] source.js fileList', e)
          })
        break
      default:
        resolve()
        break
    }
  })

  return p
}

export async function getSourceData(text, jsonPage) {
  var matches = cmsData.regex.getTagAbeWithSource(text)

  const result = await Promise.all(
    matches.map(async match => {
      await nextSourceData(jsonPage, match)
    })
  )

  return result
}

export async function getDataList(text, jsonPage) {
  var matches = cmsData.regex.getTagAbeTypeRequest(text)
  const result = await Promise.all(
    matches.map(async match => {
      await nextDataList(jsonPage, match[0])
    })
  )

  return cmsData.source.getSourceData(text, jsonPage)
}

export function removeDataList(text) {
  return text.replace(cmsData.regex.dataTypeReg, '')
}

export function removeNonEditableDataList(text) {
  return text.replace(cmsData.regex.nonEditableDataReg, '')
}

/**
 * Remove all type='data' outside {{#each}} statements
 * @param  {[type]} text [description]
 * @return {[type]}      [description]
 */
export function removeNonEachDataList(text) {
  // removing each blocks potentially containing abe data type
  let pattEach = /(\{\{#each (\r|\t|\n|.)*?\/each\}\})/g
  let textWithNoEach = text.replace(pattEach, '')

  var match
  while ((match = cmsData.regex.dataTypeReg.exec(textWithNoEach))) {
    text = text.replace(match[0], '')
  }

  return text
}

import path from "path";
import _ from "lodash";

import { config, mongo, cmsData, abeExtend, coreUtils, Manager } from "../../";

/**
 * Create and array of doc file path containing the versions of this doc
 * @param  {String} path of a doc
 * @return {Array} the versions of the doc
 */
export function getVersions(docPath) {
  let result = [];
  const files = Manager.instance.getList();
  var jsonPath = docPath.replace(
    "." + config.files.templates.extension,
    ".json"
  );
  if (files != null) {
    Array.prototype.forEach.call(files, (file) => {
      if (file.path.startsWith(jsonPath)) {
        result = file.revisions;
      }
    });
  }
  return result;
}

/**
 * Return the revision from document html file path
 * if the docPath contains abe revision [status]-[date] will try to return this revision
 * else it will return the latest revision
 * or null
 *
 * @param  {String} html path
 * @return {Object} file revision | null
 */
export function getDocumentRevision(docPath) {
  var result = null;
  var documentPath = docPath;
  var latest = true;

  if (cmsData.fileAttr.test(documentPath)) {
    latest = false;
    documentPath = cmsData.fileAttr.delete(documentPath);
  }
  var revisions = getVersions(documentPath);
  if (latest && revisions.length >= 0) {
    result = revisions[0];
  }

  return result;
}

export function getStatusAndDateToFileName(date) {
  var res =
    date.substring(0, 4) +
    "-" +
    date.substring(4, 6) +
    "-" +
    date.substring(6, 11) +
    ":" +
    date.substring(11, 13) +
    ":" +
    date.substring(13, 15) +
    "." +
    date.substring(15, 19);
  return res;
}

export function removeStatusAndDateFromFileName(date) {
  return date.replace(/[-:\.]/g, "");
}

export function filePathInfos(pathFolder) {
  var pathArr = pathFolder.split("/");
  var name = pathArr[pathArr.length - 1];

  var rootArr = config.root.split("/");
  var website = rootArr[pathArr.length - 1];
  return {
    name: name,
    path: pathFolder,
    website: website,
    type: "folder",
  };
}

export function mergeRevisions(files) {
  let merged = {};

  Array.prototype.forEach.call(files, (file) => {
    const docRelativePath = cmsData.utils.getDocRelativePath(file.path);

    if (merged[docRelativePath] == null) {
      merged[docRelativePath] = {
        path: cmsData.utils.getDocRelativePath(file.path),
        revisions: [],
      };
    }
    merged[docRelativePath].revisions.push(JSON.parse(JSON.stringify(file)));
  });

  return merged;
}

export function sortRevisions(merged) {
  let sortedArray = [];

  Array.prototype.forEach.call(Object.keys(merged), (key) => {
    let revisions = merged[key].revisions;
    revisions.sort(coreUtils.sort.predicatBy("date", -1));
    merged[key].revisions = revisions;

    if (revisions[0] != null) {
      merged[key].name = revisions[0].name;
      merged[key].date = revisions[0].date;
      merged[key].abe_meta = revisions[0].abe_meta;
    }

    let newStatuses = [];
    Array.prototype.forEach.call(revisions, (revision) => {
      const revisionStatus = _.get(revision, "abe_meta.status");

      if (revisionStatus && revisionStatus != "") {
        if (!(revisionStatus in newStatuses)) {
          if (revisionStatus === "publish") {
            merged[key][revisionStatus] = revision;
          } else {
            merged[key][revisionStatus] = {};
          }
          merged[key][revisionStatus].path = revision.path;
          merged[key][revisionStatus].date = revision.date;
          merged[key][revisionStatus].link = revision.abe_meta.link;

          newStatuses[revisionStatus] = true;
        }
      }
    });

    sortedArray.push(merged[key]);
  });

  return sortedArray;
}

export function getFilesMerged(files) {
  const revisions = cmsData.revision.mergeRevisions(files);

  return cmsData.revision.sortRevisions(revisions);
}

