import {cmsData, config} from '../../'

export function add(json, type, date = null, user = null) {
  const meta = config.meta.name
  const currentDate = date != null && date !== '' ? date : new Date()
  //const abeUrl = (type === 'publish') ? json[meta].link : cmsData.fileAttr.add(json[meta].link, type[0] + cmsData.revision.removeStatusAndDateFromFileName(currentDate.toISOString())) + ''

  if (json['abeEditor']) delete json['abeEditor']
  if (json['oldFilePath']) delete json['oldFilePath']

  json[meta].updatedDate = currentDate
  json[meta].updatedBy = user === null ? 'admin' : user.username
  json[meta].status = type
}

export function create(json, template, url, user = null) {
  const meta = config.meta.name
  if (json[meta] == null) {
    json[meta] = {}
  }

  json[meta].template = template
  json[meta].link = url
  json[meta].createdDate = new Date()
  json[meta].createdBy = user === null ? 'admin' : user.username

  return json
}

import path from 'path'
import mkdirp from 'mkdirp'
import fse from 'fs-extra'

import {abeExtend, coreUtils, cmsData, config, Manager} from '../../'

export function getAbeMeta(fileObject, json) {
  if (json.name != null) {
    fileObject.name = json.name
  }

  if (json.abe_meta != null) {
    var date = null

    if (json.abe_meta.updatedDate != null) {
      fileObject.date = json.abe_meta.updatedDate
      date = json.abe_meta.updatedDate
    } else if (
      json.abe_meta.latest != null &&
      json.abe_meta.latest.date != null
    ) {
      fileObject.date = json.abe_meta.latest.date
      date = json.abe_meta.latest.date
    } else if (json.abe_meta.date !== null) {
      date = json.abe_meta.date
    }

    fileObject.abe_meta = {
      date: date,
      link: json.abe_meta.link != null ? json.abe_meta.link : null,
      template: json.abe_meta.template != null ? json.abe_meta.template : null,
      status: json.abe_meta.status != null ? json.abe_meta.status : null
    }
  }

  return fileObject
}

export async function getAllWithKeys(withKeys) {
  const extension = '.json'
  const files = await coreUtils.file.getFiles(
    Manager.instance.pathData,
    true,
    extension
  )

  let filesArr = []

  await Promise.all(files.map(async pathFile => {
    const json = await cmsData.revision.getDoc(pathFile)
    let fileObject = cmsData.file.getFileObject(pathFile, json)
    fileObject = cmsData.file.getAbeMeta(fileObject, json)
    Array.prototype.forEach.call(withKeys, key => {
      fileObject[key] = json[key]
    })

    filesArr.push(fileObject)
  }));

  return filesArr
}

export async function get(pathJson) {
  pathJson = cmsData.utils.getRevisionPath(pathJson)
  let json = {}
  pathJson = abeExtend.hooks.instance.trigger('beforeGetJson', pathJson)

  try {
    var stat = await fse.statSync(pathJson)
    if (stat) {
      json = fse.readFileSync(pathJson)
      json = JSON.parse(json)
    }
  } catch (e) {
    //console.log('error get', e)
    json = {}
  }

  json = abeExtend.hooks.instance.trigger('afterGetJson', json)
  return json
}

export function getFilesByType(pathFile, type = null) {
  const extension = '.' + config.files.templates.extension
  let result = []

  try {
    var directory = fse.lstatSync(pathFile)
    if (!directory.isDirectory()) {
      mkdirp.sync(pathFile)
    }
  } catch (e) {
    mkdirp.sync(pathFile)
  }
  const files = coreUtils.file.getFilesSync(pathFile, true, extension)

  Array.prototype.forEach.call(files, file => {
    var val = cmsData.fileAttr.get(file).s
    if (type === null || val === type) result.push(file)
  })

  return result
}

export function getFileObject(relativePath, json) {
  const pathFile = cmsData.utils.getRevisionPath(relativePath)

  let name = path.basename(pathFile)
  name = cmsData.fileAttr.delete(name)

  let fileObject = {
    name: name,
    date: cmsData.fileAttr.getDate(pathFile),
    path: pathFile
  }

  return fileObject
}

import path from 'path'

import {config, cmsData, Manager} from '../../'

export function getPercentOfRequiredTagsFilled(text, json) {
  var regAbe = /{{abe[\S\s].*?key=['|"]([\S\s].*?['|"| ]}})/g
  var matches = text.match(regAbe)
  var requiredValue = 0
  var complete = 0
  if (typeof matches !== 'undefined' && matches !== null) {
    Array.prototype.forEach.call(matches, match => {
      if (typeof match !== 'undefined' && match !== null) {
        var keyAttr = cmsData.regex.getAttr(match, 'key')
        var requiredAttr = cmsData.regex.getAttr(match, 'required')
        if (requiredAttr === 'true') {
          requiredValue++

          var minAttr = cmsData.regex.getAttr(match, 'min-length')
          minAttr = minAttr !== '' ? minAttr : 0

          if (
            typeof json[keyAttr] !== 'undefined' &&
            json[keyAttr] !== null &&
            json[keyAttr] !== ''
          ) {
            if (minAttr > 0) {
              if (json[keyAttr].length >= minAttr) {
                complete++
              }
            } else {
              complete++
            }
          }
        }
      }
    })
  }

  return Math.round(requiredValue > 0 ? complete * 100 / requiredValue : 100)
}

/**
 * Adds the data absolute path to the jsonPath
 * ex. jsonPath = fr/test-abe-d20170418T130602280Z.json
 * return /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * and returns this relative path to the json revision
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getRevisionPath(jsonPath) {
  return path.join(Manager.instance.pathData, getRevisionRelativePath(jsonPath))
}

/**
 * Remove the data absolute path from the jsonPath
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return fr/test-abe-d20170418T130602280Z.json
 * and returns this relative path to the json revision
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getRevisionRelativePath(jsonPath) {
  return jsonPath.replace(Manager.instance.pathData + path.sep, '')
}

/**
 * Return the absolute path of the doc from the post URL
 * @param  {[type]} postUrl [description]
 * @return {[type]}         [description]
 */
export function getDocPathFromPostUrl(postUrl) {
  const extension = '.json'
  const templateExtension = '.' + config.files.templates.extension
  const osJsonPath = postUrl
    .replace(templateExtension, extension)
    .replace('/', path.sep)
  return path.join(Manager.instance.pathData, osJsonPath)
}

/**
 * Return the relative path of the doc from the post URL
 * @param  {[type]} postUrl [description]
 * @return {[type]}         [description]
 */
export function getDocRelativePathFromPostUrl(postUrl) {
  let docPath = getDocPathFromPostUrl(postUrl)

  return getDocRelativePath(docPath)
}

/**
 * Return the absolute path of the doc from a post URL, a status and a date
 * @param  {[type]} postUrl [description]
 * @return {[type]}         [description]
 */
export function getDocPathFromLinkStatusDate(jsonData) {
  let jsonObject = cmsData.file.getAbeMeta({}, jsonData)
  let dateIso = jsonObject.abe_meta.date
  if (typeof dateIso === 'object') {
    dateIso = dateIso.toISOString()
  }
  const statusDate =
    jsonObject.abe_meta.status[0] +
    cmsData.revision.removeStatusAndDateFromFileName(dateIso)
  return getDocPathFromPostUrl(
    cmsData.fileAttr.add(jsonObject.abe_meta.link, statusDate)
  )
}

/**
 * Remove the date revision from the jsonPath and returns the doc full path (which is the published json)
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return /Users/grg/programmation/git/abetesttheme/data/fr/test.json
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getDocPath(jsonPath) {
  return cmsData.fileAttr.delete(jsonPath)
}

/**
 * Remove the date revision from the jsonPath and returns the doc relative path (which is the published json)
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return fr/test.json
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getDocRelativePath(jsonPath) {
  return cmsData.fileAttr.delete(getRevisionRelativePath(jsonPath))
}

/**
 * Remove the date revision from the jsonPath and returns the html full path (which is the published post)
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return /Users/grg/programmation/git/abetesttheme/site/fr/test.html
 * can be:
 * - the full path to the revision : /Users/myname/myAbeBlog/data/fr/abe/is/awesome-d-20170808020202TZ.json
 * - the relative path to the revision : fr/abe/is/awesome-d-20170808020202TZ.json
 * - the full path to the doc : /Users/myname/myAbeBlog/data/fr/abe/is/awesome.json
 * - the relative path to the doc : fr/abe/is/awesome.json
 * - the relative path to the post : fr/abe/is/awesome.html
 *
 * will return : /Users/myname/myAbeBlog/site/fr/abe/is/awesome.html
 * 
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getPostPath(jsonPath) {
  return path.join(Manager.instance.pathPublish, getPostRelativePath(jsonPath))
}

/**
 * Remove the date revision from the jsonPath and returns the html relative path (which is the published post)
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return fr/test.html (under window it could be fr\test.html)
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getPostRelativePath(jsonPath) {
  const extension = '.json'
  const templateExtension = '.' + config.files.templates.extension

  return getDocRelativePath(jsonPath).replace(extension, templateExtension)
}

/**
 * Remove the date revision from the jsonPath and returns the URL absolute path of the published post)
 * ex. jsonPath = /Users/grg/programmation/git/abetesttheme/data/fr/test-abe-d20170418T130602280Z.json
 * return fr/test.html
 * @param  {[type]} jsonPath [description]
 * @return {[type]}          [description]
 */
export function getPostUrl(jsonPath) {
  const extension = '.json'
  const templateExtension = '.' + config.files.templates.extension

  return path.join(
    '/',
    getPostRelativePath(jsonPath).replace(extension, templateExtension)
  )
}

/**
 * sort an array of objects by date attribute
 * 
 * @param  {Object} a object with date attribute
 * @param  {Object} b object with date attribute
 * @return {Int}   1 | 0 | -1
 */
export function byDateDesc(a, b) {
  var dateA = a.abe_meta.date != null ? new Date(a.abe_meta.date) : 0
  var dateB = b.abe_meta.date != null ? new Date(b.abe_meta.date) : 0
  if (dateA < dateB) {
    return 1
  } else if (dateA > dateB) {
    return -1
  }
  return 0
}

/**
 * sort an array of objects by date attribute
 *
 * @param  {Object} a object with date attribute
 * @param  {Object} b object with date attribute
 * @return {Int}   1 | 0 | -1
 */
export function byDateAsc(a, b) {
  var dateA = a.abe_meta.date != null ? new Date(a.abe_meta.date) : 0
  var dateB = b.abe_meta.date != null ? new Date(b.abe_meta.date) : 0
  if (dateA > dateB) {
    return 1
  } else if (dateA < dateB) {
    return -1
  }
  return 0
}

