import request from 'request'
import extract from 'extract-zip'

import {config, cmsTemplates, coreUtils} from '../../'

export function getThemeInfos() {
  var pathToTheme = path.join(
    config.root,
    config.themes.path,
    config.themes.name
  )

  var p = new Promise(resolve => {
    fse.readJson(path.join(pathToTheme, 'theme.json'), (err, json) => {
      // if (err) console.error(err)
      var json = json || {theme: {}}
      resolve(json.theme)
    })
  }).catch(function(e) {
    console.error(e)
  })

  return p
}

export function deleteTheme() {
  var pathToTheme = path.join(
    config.root,
    config.themes.path,
    config.themes.name
  )
  fse.readJson(path.join(pathToTheme, 'theme.json'), (err, json) => {
    if (err) console.error(err)
    if (json != null && json.theme != null && json.theme.root_files != null) {
      Array.prototype.forEach.call(json.theme.root_files, file => {
        fse.remove(path.join(pathToTheme, file), err => {
          if (err) console.error(err)
        })
      })
    }
  })
}

export function downloadTheme(url, name) {
  const pathToThemes = path.join(config.root, config.themes.path)
  const PathToTmpTheme = path.join(pathToThemes, 'tmp-download')
  mkdirp.sync(PathToTmpTheme)

  const p = new Promise(resolve => {
    const pathToZip = path.join(PathToTmpTheme, name + '.zip')
    const writeStream = fse.createWriteStream(pathToZip)

    request(url)
      .on('response', res => {})
      .on('error', res => {
        resolve({res: 'ko', error: res})
      })
      .on('end', res => {})
      .pipe(writeStream)
    writeStream.on('finish', function() {
      extract(pathToZip, {dir: PathToTmpTheme}, function(err) {
        const dirs = coreUtils.file.getFoldersSync(PathToTmpTheme, false)
        const currentPathToTheme = dirs[0].path
        fse.removeSync(path.join(pathToThemes, name), err => {
          if (err) return console.error(err)
        })
        fse.renameSync(currentPathToTheme, path.join(pathToThemes, name))

        fse.remove(PathToTmpTheme, err => {
          if (err) return console.error(err)
        })

        if (err != null) {
          console.log(err)
          resolve({res: 'ko', error: 'err'})
          return
        }

        let json = config.getLocalConfig()
        json.themes = {
          name: name
        }
        config.save(json)
        cmsTemplates.assets.copy()
        getThemeInfos().then(json => {
          resolve({success: 'ok', theme: json})
        })
      })
    })
  }).catch(function(e) {
    resolve({res: 'ko', error: e})
    console.error(e)
  })

  return p
}

import execPromise from 'child-process-promise'
import mkdirp from 'mkdirp'
import fse from 'fs-extra'
import slug from 'slugify'
import Jimp from 'jimp'
import path from 'path'
import {Promise} from 'bluebird'
import smartcrop from 'smartcrop-jimp'

import {abeExtend, coreUtils, cmsData, config, Manager} from '../../'

export function cropAndSaveFile(imageSize, file, newFile) {
  var p = new Promise(resolve => {
    smartcrop.crop(file, { width: parseInt(imageSize[0]), height: parseInt(imageSize[1]) })
      .then(function(result) {
        var crop = result.topCrop;
        Jimp.read(file)
          .then(function(image) {
            image
            .crop(crop.x, crop.y, crop.width, crop.height)
            .write(newFile)
          });
      })
      .catch(function(err) {
        console.error(err)
      });

    resolve()
  })
  return p
}

// export function smartCropAndSaveFile(imageSize, file, newFile) {
//   var cmd = `node node_modules/smartcrop-cli/smartcrop-cli.js --width ${parseInt(
//     imageSize[0]
//   )} --height ${parseInt(imageSize[1])} ${file} ${newFile}`
//   var p = execPromise.exec(cmd)
//   return p
// }

export function cropAndSaveFiles(images, file, resp) {
  var length = images.length
  var cropedImage = 0
  resp.thumbs = []
  var p = new Promise(resolve => {
    for (var i = 0; i < length; i++) {
      let image = images[i]
      let ext = path.extname(file)
      let newFile = file.replace(ext, `_${images[i]}${ext}`)
      resp.thumbs.push({
        name: newFile.replace(Manager.instance.pathPublish, ''),
        size: image
      })

      let splitedImage = image.split('x')
      let newWidth = null
      let newHeight = null

      if (splitedImage[0] != null && splitedImage[0] != '')
        newWidth = parseInt(image.split('x')[0])
      if (splitedImage[1] != null && splitedImage[1] != '')
        newHeight = parseInt(image.split('x')[1])

      Jimp.read(file)
        .then(function(originalImage) {
          var originalWidth = originalImage.bitmap.width
          var originalHeight = originalImage.bitmap.height
          var ratio = originalWidth * newHeight / newWidth
          if (newWidth === null || newHeight === null) {
            originalImage
              .resize(
                newWidth != null ? newWidth : Jimp.AUTO,
                newHeight != null ? newHeight : Jimp.AUTO
              )
              .write(newFile)
            if (++cropedImage === length) {
              resolve(resp)
            }
          } else if (
            parseInt(ratio - 1) <= parseInt(originalHeight) &&
            parseInt(ratio + 1) >= parseInt(originalHeight)
          ) {
            originalImage
              .resize(
                parseInt(image.split('x')[0]),
                parseInt(image.split('x')[1])
              )
              .write(newFile)
            if (++cropedImage === length) {
              resolve(resp)
            }
          } else {
            cropAndSaveFile(image.split('x'), file, newFile)
            .then(function(result) {
              if (++cropedImage === length) {
                resolve(resp)
              }
            })
            .catch(function(err) {
              console.log(err)
            })
          }
        })
        .catch(function(err) {
          console.log(err)
        })
    }
  })

  return p
}

export const generateThumbnail = async (file) => {
  var ext = path.extname(file).toLowerCase()
  var thumbFileName = file.replace(ext, `_thumb${ext}`)
  var thumbFileNameRelative = thumbFileName.replace(
    Manager.instance.pathPublish,
    ''
  )
  var p = new Promise(resolve => {
    var cropThumb = cropAndSaveFile([250, 250], file, thumbFileName)
    cropThumb.then(function(result) {
      resolve({thumb: thumbFileNameRelative})
    })
  })

  return p
}

export function saveFile(req) {
  var p = new Promise(resolve => {
    const keepName = (req.query.keepName === 'true')
    var resp = {success: 1}
    var filePath
    req.pipe(req.busboy)
    req.busboy.on('file', function(
      fieldname,
      file,
      filename,
      encoding,
      mimetype
    ) {
      var ext = path.extname(filename).toLowerCase()
      var slug = createMediaSlug(filename, ext, keepName)
      var mediaType = getMediaType(ext)

      var folderFilePath = createMediaFolder(mediaType)
      var hasSentHeader = false
      var folderWebPath = '/' + config.upload.image
      if (config.upload[mediaType] != null) {
        folderWebPath = '/' + config.upload[mediaType]
      }

      filePath = path.posix.join(folderFilePath, slug)
      resp['filePath'] = path.posix.join('/' + folderWebPath, slug)

      file.on('limit', function() {
        hasSentHeader = true
        file.resume()
        resolve({error: 1, response: 'file is too big'})
        return
      })

      var isValid = isValidMedia(mimetype, ext)
      if (isValid.error) {
        hasSentHeader = true
        file.resume()
        resolve({error: 1, response: isValid.error})
        return
      }

      var fstream = fse.createWriteStream(filePath)
      fstream.on('finish', async () => {
        if (hasSentHeader) return
        if (mediaType === 'image') {
          const thumbResp = await generateThumbnail(filePath)
          resp.thumbnail = /^win/.test(process.platform)
            ? thumbResp.thumb.replace(/\\/g, '/')
            : thumbResp.thumb

          resp = abeExtend.hooks.instance.trigger('afterSaveImage', resp, req)
          if (
            req &&
            req.query &&
            req.query.input &&
            req.query.input.indexOf('data-size') > -1
          ) {
            var thumbsSizes = cmsData.regex
              .getAttr(req.query.input, 'data-size')
              .replace(' ', '')
              .split(',')
            cropAndSaveFiles(thumbsSizes, filePath, resp).then(function(resp) {
              if (/^win/.test(process.platform)) {
                for (var i = 0; i < resp.thumbs.length; i++) {
                  resp.thumbs[i].name = resp.thumbs[i].name.replace(/\\/g, '/')
                }
              }
              resolve(resp)
            })
          } else resolve(resp)
          //})
        } else {
          resolve(resp)
        }
      })
      file.pipe(fstream)
    })
  })

  return p
}

export function isValidMedia(mimetype, ext) {
  var allowedExtensions = config.upload.extensions
  var allowedMimetypes = config.upload.mimetypes

  var error = false
  if (allowedMimetypes.indexOf(mimetype) < 0)
    error = ext + ' is not an authorized mimetype'
  else if (allowedExtensions.indexOf(ext) < 0)
    error = ext + ' is not an authorized extension'

  return {error: error}
}

export function getMediaType(ext) {
  let type = 'document'

  if (/\.(jpg|jpeg|png|gif|svg|webp)/.test(ext)) {
    type = 'image'
  } else if (/\.(mov|avi|mp4)/.test(ext)) {
    type = 'video'
  } else if (/\.(mp3|wav)/.test(ext)) {
    type = 'sound'
  }

  return type
}

export function createMediaSlug(filename, ext, keepName = false) {
  if (!keepName) {
    const filenameNoExt = path.basename(filename, ext).toLowerCase()
    return (`${slug(filenameNoExt, { remove: /[$*+~.()'"!\:@§^,;]/g })}-${coreUtils.random.generateUniqueIdentifier(2)}${ext}`)
  }

  return filename
}

export function createMediaFolder(mediaType) {
  var folderWebPath = '/' + config.upload.image
  if (config.upload[mediaType] != null) {
    folderWebPath = '/' + config.upload[mediaType]
  }
  folderWebPath = abeExtend.hooks.instance.trigger(
    'beforeSaveImage',
    folderWebPath
  )
  var folderFilePath = path.join(Manager.instance.pathPublish, folderWebPath)
  mkdirp.sync(folderFilePath)

  return folderFilePath
}

export function getThumbsList() {
  var thumbsList = []
  var pathToThumbs = path.join(
    Manager.instance.pathPublish,
    config.upload.image
  )
  var files = coreUtils.file.getFilesSync(pathToThumbs, true)
  Array.prototype.forEach.call(files, pathFile => {
    pathFile = pathFile.replace(Manager.instance.pathPublish, '')
    if (pathFile.indexOf('_thumb.') > -1) {
      thumbsList.push({
        originalFile: pathFile.replace('_thumb.', '.'),
        thumbFile: pathFile
      })
    }
  })

  return thumbsList
}

export function getAssociatedImageFileFromThumb(name) {
  var rexMatchImageName = /_(thumb|\d+x\d+)\./
  name = path.join(path.sep, name)
  var originalName = path.join(path.sep, name.replace(rexMatchImageName, '.'))
  var imageList = {
    thumbFile: name,
    originalFile: originalName,
    thumbs: []
  }
  var pathThumb = name.split('/')
  pathThumb.pop()
  pathThumb = path.join(Manager.instance.pathPublish, pathThumb.join('/'))

  var files = coreUtils.file.getFilesSync(pathThumb, true)
  Array.prototype.forEach.call(files, pathFile => {
    pathFile = pathFile.replace(Manager.instance.pathPublish, '')
    if (
      pathFile !== originalName &&
      pathFile !== name &&
      pathFile.replace(rexMatchImageName, '.') === originalName
    ) {
      imageList.thumbs.push(pathFile)
    }
  })

  return imageList
}

/**
 * Highly efficient filter on a value in an object
 * @param  {Array} arr   the array of objects
 * @param  {string} attr the attribute to filter on
 * @param  {mixed} value the value to compare
 * @return {Array}       the filtered array
 */
export function filter(arr, attr, value) {
  const len = arr.length
  let result = []
  let i = 0

  for (; i < len; i += 1) {
    let elt = arr[i]

    if (elt[attr] == value) {
      result.push(elt)
    }
  }
  return result
}

/**
 * Highly efficient facet on a value in an object
 * Works also for nested properties
 * @param  {Array} arr   the array of objects to search
 * @param  {Array} attrs the attributes to facet on
 * @param  {mixed} value the value to compare
 * @return {Array}       the filtered array
 */
export function facet(arr, attrs, value) {
  let searches = []
  Array.prototype.forEach.call(attrs, attr => {
    let attrPath = attr.split('.')
    searches.push(attrPath)
  })

  const len = arr.length
  let result = []
  let i = 0

  for (; i < len; i += 1) {
    let elt = arr[i]
    let found = false
    Array.prototype.forEach.call(searches, search => {
      let j = 0
      let a = arr[i]
      while (j < search.length) {
        a = a[search[j]]
        j++
      }
      if (a.indexOf(value) !== -1) {
        found = true
      }
    })

    if (found) {
      result.push(elt)
    }
  }

  return result
}

/**
 * Highly efficient find indexes on a value in a property of an object
 * Works also for nested properties
 * @param  {Array} arr   the array of objects
 * @param  {string} attr the attribute to filter on
 * @param  {mixed} value the value to compare
 * @return {Array}       the filtered array of indexes
 */
export function find(arr, attr, value) {
  const len = arr.length
  let result = []
  let i = 0

  for (; i < len; i += 1) {
    let elt = arr[i]

    if (elt[attr] == value) {
      result.push(i)
    }
  }
  return result
}

/**
 * Remove objects from an array given an attribute value
 * @param  {Array} arr   the array of objects
 * @param  {string} attr the attribute to filter on
 * @param  {mixed} value the value to compare
 * @return {Array}       the array with corresponding objects removed
 */
export function removeByAttr(arr, attr, value) {
  let i = arr.length
  while (i--) {
    if (
      arr[i] &&
      arr[i].hasOwnProperty(attr) &&
      (arguments.length > 2 && arr[i][attr] === value)
    ) {
      arr.splice(i, 1)
    }
  }

  return arr
}

export function contains(arr, obj) {
  var i = arr.length
  while (i--) {
    if (arr[i] === obj) {
      return true
    }
  }
  return false
}

/**
 * shuffle an array of objects by date attribute
 *
 * Example : var shuffledArray = coreUtils.sort.shuffle([Object}, Object])
 * 
 * @param  {Array} array
 * @return {Array} array
 */
export function shuffle(array) {
  var currentIndex = array.length,
    temporaryValue,
    randomIndex

  // While there remain elements to shuffle...
  while (0 !== currentIndex) {
    // Pick a remaining element...
    randomIndex = Math.floor(Math.random() * currentIndex)
    currentIndex -= 1

    // And swap it with the current element.
    temporaryValue = array[currentIndex]
    array[currentIndex] = array[randomIndex]
    array[randomIndex] = temporaryValue
  }

  return array
}

/**
 * This function makes sorting on an array of Json objects possible.
 * Pass the property to be sorted on.
 * Usage: myArray.sort(coreUtils.sort.predicatBy('date',-1));
 * @param  String prop  the json property to sort on
 * @param  integer order order ASC if 1, DESC if -1
 * @return integer the ordered value
 */
export function predicatBy(prop, order) {
  prop = prop.split('.')
  var len = prop.length

  if (order !== -1) {
    order = 1
  }
  if (prop === 'date') {
    return function(a, b) {
      a = new Date(a[prop])
      b = new Date(b[prop])
      if (a > b) {
        return 1 * order
      } else if (a < b) {
        return -1 * order
      }
      return 0
    }
  }

  return function(a, b) {
    var i = 0
    while (i < len) {
      a = a[prop[i]]
      b = b[prop[i]]
      i++
    }
    if (!isNaN(a)) a = parseFloat(a)
    if (!isNaN(b)) b = parseFloat(b)
    if (a < b) {
      return -1 * order
    } else if (a > b) {
      return 1 * order
    }
    return 0
  }
}

import fse from 'fs-extra'
