import process from 'child_process'
import fse from 'fs-extra'

import {config, abeExtend, Manager} from '../'

function prepend(value, array) {
  var newArray = array.slice(0)
  newArray.unshift(value)
  return newArray
}

var abeProcess = function(name, args = [], callback) {
  args = prepend(`ABE_WEBSITE=${config.root}`, args)
  args = prepend(`ABECMS_PATH=${__dirname}/../../../dist`, args)

  if (Manager.instance.isProcessRunning(name)) {
    return false
  }

  Manager.instance.addProcess(name)

  var proc
  var file = `${__dirname}/../../cli/process/${name}.js`
  try {
    var stats = fse.statSync(file)
    if (stats.isFile()) {
      proc = process.fork(file, args)
    }
  } catch (err) {
    try {
      file = abeExtend.plugins.instance.getProcess(name)
      stats = fse.statSync(file)
      if (stats.isFile()) {
        proc = process.fork(file, args)
      }
    } catch (err) {
      Manager.instance.removeProcess(name)
      console.log(
        `Manager.instance.addProcess : process fork failed on ${__dirname}/../../cli/process/${name}.js`
      )
    }
  }

  if (typeof proc !== 'undefined' && proc !== null) {
    proc.on('message', function(data) {
      var data = JSON.parse(data)
      callback(data)
      if (data.msg == 'exit') {
        Manager.instance.removeProcess(name)
        proc.kill()
      }
    })
    return true
  }

  return false
}

export default abeProcess

import fs from 'fs-extra'
import mkdirp from 'mkdirp'
import path from 'path'

import {config, coreUtils, User} from '../../cli'

let singleton = Symbol()
let singletonEnforcer = Symbol()

class Manager {
  constructor(enforcer) {
    if (enforcer != singletonEnforcer) throw 'Cannot construct Json singleton'

    this._file = path.join(config.root, config.users.path)
  }

  static get instance() {
    if (!this[singleton]) {
      this[singleton] = new Manager(singletonEnforcer)
    }
    return this[singleton]
  }

  /** TODO MONGO */
  read() {
    if (this.isActive()) {
      if (coreUtils.file.exist(this._file)) {
        return JSON.parse(fs.readFileSync(this._file, 'utf8'))
      } else {
        this._users = []
        var admin = User.operations.add(config.users.default)
        this.save()
        User.operations.activate(admin.user.id)
        return JSON.parse(fs.readFileSync(this._file, 'utf8'))
      }
    }
    return []
  }

  /** TODO MONGO */
  save() {
    if (this.isActive()) {
      mkdirp.sync(path.dirname(this._file))
      fs.writeJsonSync(this._file, this._users, {space: 2, encoding: 'utf-8'})
    }
  }

  /**
   * TODO MONGO
   */
  get() {
    if (this._users == null) {
      this._users = this.read()
    }
    return this._users
  }

  findById(id) {
    var bdd = this.get()
    id = parseInt(id)
    for (var i = 0, len = bdd.length; i < len; i++) {
      var user = bdd[i]
      if (parseInt(user.id) === id) {
        return user
      }
    }

    return false
  }

  findByEmail(email) {
    var bdd = this.get()
    id = parseInt(id)
    for (var i = 0, len = bdd.length; i < len; i++) {
      var user = bdd[i]
      if (user.email === email) {
        return user
      }
    }

    return false
  }

  findByResetPasswordToken(resetPasswordToken) {
    var bdd = this.get()
    id = parseInt(id)
    for (var i = 0, len = bdd.length; i < len; i++) {
      var user = bdd[i]
      if (user.resetPasswordToken === resetPasswordToken) {
        return user
      }
    }

    return false
  }

  /**
   * TODO MONGO
   * @param {*} json 
   */
  update(json) {
    if (this.isActive()) {
      this._users = json
      this.save()
      return true
    }

    return false
  }

  isActive() {
    return config.users.enable
  }
}

